import React, { useEffect, useState } from "react";
import "./SearchResult.css";
import ProductCard from "../../Component/ProductCard/ProductCard.js";
import { instanceAxs } from "../../config/api";
import { useLocation } from "react-router-dom";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Filters from "./Filters";
import Form from 'react-bootstrap/Form';
import Button from "react-bootstrap/Button";
import Badge from 'react-bootstrap/Badge';
import FilterBadge from "./FilterBadge";

const SearchResult = () => {

  const [productArray, setProductArray] = useState([]);
  const location = useLocation();
  
  const [filters, setFilters] = useState([{
    queryKey: 'q',
    queryValue:  new URLSearchParams(location.search).get("q"),
  }]);

  const handleFilterChange = (key, value) => {
    let filter = {
      queryKey: key,
      queryValue: value
    }
    const isFilterExists = filters.find(item => item.queryKey === key);
    if (isFilterExists) {
      const newFilters = filters.filter(item => item.queryKey !== key);
      newFilters.push(filter);
      return setFilters(newFilters)
    }

    setFilters([...filters, filter])
  }

  const removeSelectedFilter = (key) => {
    const newFilters = filters.filter(item => item.queryKey !== key);
    return setFilters(newFilters)
  }

  useEffect(() => {
    console.log(filters)
    console.log(productArray)
  }, [filters])

  const handleSortingChange = (e) => {
    let value = e.target.value;
    var finalArray = productArray;
    console.log(productArray)
    if(value === "price_desc") {
      let priceDescArray = productArray
     .sort((a, b) => a.price > b.price ? 1 : -1)
     console.log(priceDescArray)
      finalArray = priceDescArray;
    } else if(value === "price_asc") {
      let priceAscArray = [].concat(finalArray)
    .sort((a, b) => a.price < b.price ? 1 : -1)
      finalArray = priceAscArray;
    } else if(value === "published-last") {
      let publishArray = [].concat(finalArray)
    .sort((a, b) => a.date < b.date ? 1 : -1)
      finalArray = publishArray;
    } else if(value === "published-first") {
      let publishArray = [].concat(finalArray)
    .sort((a, b) => a.date > b.date ? 1 : -1)
      finalArray = publishArray;
    }

    setProductArray(finalArray)
  };

/*   const getFiltersOnMount = () => {
    let urlParams = new URLSearchParams(window.location.search)

    var params = [];
    for (const [key, value] of urlParams) {
      var filter = {};

      switch(key){
        case 'q':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: undefined,
            tagValue: undefined,
          };
          params.push(filter)
          break;
        case 'price_max':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: "maxPrice",
            tagValue: `til ${value}`,
          };
          params.push(filter);
          break;
        case 'price_min':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: "minPrice",
            tagValue: `fra ${value}`,
          };
          params.push(filter);
          break;
        case 'status':
          if(value === "new") {
            filter = {
              queryKey: key,
              queryValue: value,
              tagKey: "statusNew",
              tagValue: "Nytt",
            };
          } else if(value === "used") {
            filter = {
              queryKey: key,
              queryValue: value,
              tagKey: "statusUsed",
              tagValue: "Brukt",
            };
          } 
          params.push(filter);
          break;
        case 'published':
          filter = {
            queryKey : key,
            queryValue: value,
            tagKey: key,
            tagValue: value
          }
          params.push(filter);
          break;
        default:
          
      }
    }
    this.setState({
      filters: params
    });
    return params;
  }; */

/*   const makeSearch = () => {
      this.searchAlgorithm(this.state.filters) 
  }; */

  const returnQueryString = () => {
    var queryString = "";
    for (const filter of filters) {
      queryString += `${filter.queryKey}=${filter.queryValue}&`;
    }
    if (queryString[queryString.length - 1] === "&")
      queryString = queryString.slice(0, queryString.length - 1);

    return queryString;
  }

  const searchAlgorithm = () => {
    
    let queryString = returnQueryString();
    let query = `/searchproduct?${queryString}`;
    instanceAxs.get(query).then((response) => {
        // console.log("Search response", response.data);

        if (response.error !== null) {
          const products = response.data.productArray;

          const rows = [...Array( Math.ceil(products.length / 4))];
          const productRows = rows.map( (row, idx) => products.slice(idx*4, idx * 4+ 4) );

          setProductArray(productRows)
          return window.history.pushState("page2", "seach made", `/search?${queryString}`);
        } 
        console.log(response.data.message)       
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    searchAlgorithm()
    console.log(productArray)
  }, [])

    return (
          <Container fluid className="searchresult-container">
            <Row  className="result-row">
                  <Col lg={3}  className="filters-column">
                        <Button variant="primary" className="w-100 mb-4" onClick={searchAlgorithm}>Bruk Endringer</Button>
                        <Button variant="outline-danger" className="w-100 mb-4">Reset Filters</Button>
                        <Filters handleFilterChange={handleFilterChange} removeSelectedFilter={removeSelectedFilter}></Filters>
                  </Col>

                <Col className="products-column" lg={8}>

                    <div className="top-row">
                      <p>1234 treff</p>
                      <Form.Select style={{maxWidth: 200}} onChange={handleSortingChange}>
                        <option value="mest-relevant">Mest Relevant</option>
                        <option value="puslished-first">Eldste først</option>
                        <option value="published-last">Nyeste først</option>
                        <option value="price_asc">Pris lav til høy</option>
                        <option value="price_desc">Pris høy til lav</option>
                      </Form.Select>
                    </div>

                    <div className="middle-row">
                        <FilterBadge filters={filters} removeSelectedFilter={removeSelectedFilter}></FilterBadge>
                    </div>

                      <div className="bottom-row">
                      {productArray.length > 0 && productArray.map((row, index) => {
                    return(             
                        <Row key={index} className="product-row">
                        {row.map((product, index) => {
                            return(
                                    <Col key={index} lg={4} md={6} xs={12} className="product-column">
                                        <ProductCard
                                        key={product.title}
                                        images={product.annonceImages}
                                        title={product.title}
                                        price={product.price}
                                        id={product._id}
                                        location={product.location}
                                        ></ProductCard>
                                </Col>
                            )
                        })}
                    </Row>                 
                    )
                })               
                }
          
                      </div>
                  </Col>
            </Row>
          </Container>
    );
  
}

export default SearchResult;
