import React from "react";
import { Link , withRouter} from "react-router-dom";
import logo from "../../resources/logo.svg";
import "./Navbar.css";
import { connect } from "react-redux";

class Navbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentY: 0,
      isRender: true,
      isLoggedIn: false,
      searchInput: "",
    };
    this.scroll = this.scroll.bind(this);
  }

  checkCharacters = async () => {
      let input = this.state.searchInput.trim();
      let searchInput = input.replace(/\s/g, "+");
      this.setState({
          searchInput
      })
  }

  makeSearch = async () => {
      await this.checkCharacters();
      let query = `/search?q=${this.state.searchInput}`;
      this.props.history.push( query);    
      window.location.reload(false)        
  }

  scroll() {
    if (window.scrollY > this.state.currentY) {
      console.log("not rendering")
      this.setState({ isRender: false });
    } else {
      console.log("rendering")
      this.setState({ isRender: true });
    }
    this.setState({ currentY: window.scrollY });
  }

  render() {
    window.addEventListener("scroll", this.scroll);
    return (
      <div>
        {this.state.isRender &&
          <nav className="navbar navbar-expand-sm bg-dark">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link" href="#">Finn</a>
              </li>
              <li className="nav-item">
                <input type="text" placeholder="Search..."></input>
                <button>Search</button>
              </li>
              <li>
                <p style={{color: 'white'}}>Ahmet Gokpinar</p>
              </li>
            </ul>
          </nav>
        }
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.isLogged,
    username: state.user.email,
  };
};
export default connect(mapStateToProps)(withRouter(Navbar));
