import { configureStore } from '@reduxjs/toolkit'
import userSlice from '../features/userSlice';
import uiSlice from '../features/uiSlice';

const store = configureStore({
  reducer: {
    user: userSlice.reducer,
    ui: uiS
  }
})

export default store;
