import { persistReducer } from 'redux-persist'
/* import localStorage from 'redux-persist/es/storage';
import sessionStorage from 'redux-persist/es/storage/session'; */
import { combineReducers } from 'redux';
import LoginReducer from '../reducers/LoginReducer';
import userReducer from '../reducers/userReducer';
import SearchReducer from '../reducers/SearchReducer';

const persistConfig = {
  key: 'root',
  storage: localStorage,
  blacklist: ['filters']
}

export const sessionConfig = {
  key: 'user',
  storage: sessionStorage,
  blacklist: ['isLogged','user']
}

const rootReducer = combineReducers({ 
  isLogged: LoginReducer,
  user: userReducer,
  // filters: persistReducer(sessionConfig, SearchReducer)
  filters: SearchReducer
}); 

export const loadState = () => {
  try {
    const state = localStorage.getItem('state');

    if (state === null) {
      return undefined;
    } 
    return JSON.parse(state);;
  } catch(err) {
    console.log(err); 
    return undefined;
  }
}

export const saveState = (state) => {
    try {
      var userData = JSON.stringify(state.user);
      var isLoggedData = JSON.stringify(state.isLogged);
      var filtersData = JSON.stringify(state.filters);
      //sessionStorage.setItem("sessionData", filtersData)
      console.log(state)
      localStorage.setItem("localData",JSON.stringify(state))
    } catch (err) {
      console.log(err);
    }
};

export const persistedState = loadState();
export const persistedReducer = persistReducer(persistConfig, rootReducer)
