import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { instanceAxs } from "../../config/api.js";

function ProductPage() {

    let { annonceId } = useParams();
    const [annonce, setAnnonce] = useState();
    const [isLoading, setLoading] = useState(true)

    const findProduct = async () => {
        await instanceAxs.get(`/product?id=${annonceId}`)
            .then(respond => {
                console.log(respond);
                if (respond.data.message === 'An error occured while finding the product') {
                    return console.log(response)
                }
                setAnnonce(respond.data.product);
                setLoading(false)
            })   
            .catch(err => {
                console.log(err);
            })   
    }

    useEffect(() => {
        findProduct();
        //eslint-disable-next-line
    }, [isLoading])

    return(
        <div>
            { !isLoading ?
                <div>
                    <p>product details</p>
                    <h1>{annonce.title}</h1>
                    <p>id: {annonceId}</p>
                    <p>category: {annonce.category}</p>
                    <p>price: {annonce.price}</p>
                    <p>description: {annonce.description}</p>
                </div>
                :
                <p>Loading...</p>
            }
        </div>
    )
}

export default ProductPage;
