import React from 'react';
import ProductCard from '../../Component/ProductCard/ProductCard.js';
import { instanceAxs } from '../../config/api.js';
import "./Menu.css";

class Menu extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            items: [],
            isLoading: true
        }
    }

    receiveProducts = () => {
        instanceAxs.get('/search').then(response => {
            console.log(response)
            this.setState({ productArray: response.data.productArray, isLoading: false})
        })
    }
    
    componentDidMount(){
        // this.getItems();
        this.receiveProducts()
    }
    
    render(){
        return(
            <div className="container HomePageContainer">
                <div className="homePageItems">  
                    { (this.state.isLoading === false) ? 
                    
                    (this.state.productArray.map(item => {
                        var annonce = item;
                        return(
                                <div key={annonce._id}>
                                    <ProductCard 
                                        images={annonce.annonceImages} 
                                        price={annonce.price} 
                                        title={annonce.title} 
                                        id={annonce._id}
                                        location={annonce.location}/> 
                                </div>                          
                        ) 
                    }))
                    : 
                    <p>loading</p>
                }                                                      
                </div>
            </div>       
        )
    }   
}

export default Menu;
