import './Profile.css';
import React from 'react';
import { instanceAxs } from '../../../config/api.js';
import { getCroppedImage } from '../../../utils/cropImage';
import { dataURLtoFile } from '../../../utils/dataURltoFile';

class Profile extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            profileImgLink: '',
            profileImgBinary: '',
            isLoading: true
        }
    }

    onImageChange = (event) => {
        if(!event.target.files) return;   //target.files seçilen dosyaları döner
      
        const file = event.target.files;
        var profileImgBinary = null;

          var reader = new FileReader();  //Yüklenen resmin datasını oku.
          reader.readAsDataURL(file);
          reader.addEventListener('load', () => { profileImgBinary = {name: 'profilePicture', data: reader.result} })
       
        this.setState({ profileImgBinary })
      };

      convertImagesToFormData =  async () => {
        var formData = new FormData();
        const image = this.state.profileImgBinary;
      
          const canvas = await getCroppedImage(image.data);
          const canvasDataUrl = canvas.toDataURL("image/jpeg");
          const convertedUrltoFile = dataURLtoFile(canvasDataUrl, image.name);
      
          formData.append("profileImage", convertedUrltoFile);
          return formData;
        
       }

    uploadImagesToServer =  (formData, cb) => {
        instanceAxs.post('/newannonce/imageupload', formData).then(result => {
            return console.log(result)
            if (result.data.message === 'images uploaded') {
              var imageLocations = result.data.files.map(e => e.location)
              this.setState({ imageLocations })
              cb(); // run uploadAnnoncesToDb after uploading images
            }
          })
          .catch( err => console.log(err) );
    }

    getProfileImage = () => {
        instanceAxs.get('/profile/getprofileimg').then(response => {
            console.log(response)
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        this.getProfileImage();
    }

    render() {
        return(
            <div>
                {!this.state.isLoading ? 
                <div>
                    <input type="file" name="image" accept="image/*" multiple onChange={this.onImageChange} required></input>

                </div>
                :
                <p>Loading</p>}
            </div>         
        )
    }
}

export default Profile;
