import './Register.css';

import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form, Row, Col, Container, Spinner } from "react-bootstrap";
import { GoogleLogin } from "@react-oauth/google";
import "./Login.css";
import { useDispatch, useSelector } from "react-redux";
import { userActions } from "../../features/userSlice";
import {
  googleLoginRequest,
  sendLoginRequest,
} from "../../features/userSliceActions";


function Register() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const isLoggedIn = useSelector((state) => state.user.isLoggedIn);
    const [isLoading, setIsloading] = useState(false);



    //event handler olduğu için Register fonksiyonun içinde tanımlandı. 
    //başka bir tür fonnksiyon olsaydı ana fonksiyonun dışında tanımlanırdı.
    const handleSubmit = (event) => {
        event.preventDefault();
        const email = event.target[0].value;
        const password = event.target[1].value;

        dispatch(sendSignUpRequest({email, password}))        

    }


    return(
        <Container className="login-container" fluid>
        <Row className="login__row">
          <Col
            lg={5}
            className="login__chat d-flex flex-direction-column align-items-center justify-content-center"
          >
            <div className="login-div">
              <Form onSubmit={handleSubmit} className="login-form">
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="Enter email"
                    name="email"
                    required
                  />
                </Form.Group>
  
                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Password"
                    name="password"
                    required
                  />
                </Form.Group>
                <Form.Group
                  className="form-submit-remember mb-3"
                  controlId="formSubmitAndRemember"
                >
                  {isLoading ? (
                    <Button variant="primary">
                      <Spinner
                        size="sm"
                        className="me-2"
                        animation="grow"
                        as="div"
                        aria-hidden="true"
                      ></Spinner>
                      Logger Inn...
                    </Button>
                  ) : (
                    <Button variant="primary" type="submit" className="">
                      Logg Inn
                    </Button>
                  )}
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Text>
                    Har ikke en konto?{" "}
                    <a href="/register" className="signup-link">
                      Opprett en her
                    </a>
                  </Form.Text>
                </Form.Group>
  
                <hr />
                <GoogleLogin
                  onSuccess={handleGoogleAuth}
                  onFailure={handleGoogleAuth}
                  text="signin_with"
                  theme="filled_blue"
                ></GoogleLogin>
              </Form>
            </div>
          </Col>
          <Col lg={7} className="login__bg "></Col>
        </Row>
      </Container>
    )
}

export default Register;
