import React, { useState } from 'react';
import ProductCard from '../../Component/ProductCard/ProductCard.js';
import { instanceAxs } from '../../config/api.js';
import "./Menu.css";

const Menu = () => {

    const [isLoading, setIsLoading] = useState();
    const [productArray, setProductArray] = useState([])

    const receiveProducts = () => {
        instanceAxs.get('/search').then(response => {
            console.log(response)
            setProductArray(response.data.productArray)
            setIsLoading(false)
            this.setState({ productArray: response.data.productArray, isLoading: false})
        })
    }
    
    const componentDidMount = () => {
        // this.getItems();
        this.receiveProducts()
    }
    
        return(
            <div className="container HomePageContainer">
                <div className="homePageItems">  
                    { (isLoading === false) ? 
                    
                    (this.state.productArray.map(item => {
                        var annonce = item;
                        return(
                                <div key={annonce._id}>
                                    <ProductCard 
                                        images={annonce.annonceImages} 
                                        price={annonce.price} 
                                        title={annonce.title} 
                                        id={annonce._id}
                                        location={annonce.location}/> 
                                </div>                          
                        ) 
                    }))
                    : 
                    <p>loading</p>
                }                                                      
                </div>
            </div>       
        )
    }   


export default Menu;
