import React, { useState } from "react";
import "./Navbar.css";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Form, Button } from 'react-bootstrap'
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { logoutRequest } from "../../features/userSliceActions";
import Avatar from '@mui/material/Avatar';
import DropdownItem from "react-bootstrap/esm/DropdownItem";

const Navigation = () => {
  const [currentY, setCurrentY] = useState(0);
  const [isRender, setIsRender] = useState(true);
  const [searchInput, setSearchInput] = useState('a');
  const navigate = useNavigate();
  const isLoggedIn = useSelector(state => state.user.isLoggedIn)
  const email = useSelector(state => state.user.user.email);
  const user = useSelector(state => state.user.user);
  const [show, setShow] = useState(false);
  const showDropdown = (e)=>{
      setShow(!show);
  }
  const hideDropdown = e => {
      setShow(false);
  }
  const dispatch = useDispatch();
  

  const checkCharacters = async () => {
      let input = searchInput.trim().replace(/\s/g, "+");
      setSearchInput(input) 
  }

const makeSearch = async () => {
      await checkCharacters();
      let query = `/search?q=${searchInput}`;
      console.log("query",query)
      navigate( query);    
      window.location.reload(false);
  }

  const scroll = () => {
    if (window.scrollY > currentY) {
      setIsRender(false)
    } else {
      setIsRender(true)
    }
    setCurrentY( window.scrollY );
  }

  const logout = () => {
    dispatch(logoutRequest())
  }

  const navigateToProfile = () => {
    navigate('/profil')
  }

    window.addEventListener("scroll", scroll);
    return (
      <div>
        {isRender &&
          <Navbar bg="light" expand="lg" className=" ">
            <Container className="">
                <Navbar.Brand href="/">Finn</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />   
                <Navbar.Collapse id="basic-navbar-nav">         
                    {isLoggedIn ? 
                        <Nav className="d-flex align-items-center flex-grow-1 justify-content-end">
                            <Avatar alt='pp' src={user.profilePicture} sx={{width: 32, height: 32, marginRight: 1}}></Avatar>
                            <NavDropdown title={email} menuVariant='success' onClick={navigateToProfile} show={show} onMouseEnter={showDropdown} onMouseLeave={hideDropdown}>
                                <NavDropdown.Item href='/profil'>Profil</NavDropdown.Item>
                                <NavDropdown.Item href='/nyannonse'>Ny Annonse</NavDropdown.Item>
                                <NavDropdown.Item href='/meldinger'>Meldinger</NavDropdown.Item>
                                <NavDropdown.Divider/>
                                <NavDropdown.Item onClick={logout}>Log Out</NavDropdown.Item>
                            </NavDropdown>
                          </Nav>
                          :
                          <Nav className="flex-grow-1 justify-content-end">
                              <Nav.Link href='/login'>Logg Inn</Nav.Link>
                              <Nav.Link href='/register'>Register</Nav.Link>
                          </Nav>
                      }
              </Navbar.Collapse>
            </Container>
        </Navbar>
        }
      </div>
    );
  
}

