import React from "react";

import "./NewAnnonce.css";
import { getCroppedImage } from "../../utils/cropImage";
import { dataURLtoFile } from "../../utils/dataURltoFile";
import { instanceAxs } from "../../config/api";

class NewAnnonce extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        imageArray: [],
        imageLocations: [],
        annonceProperties: {}
    }
} 

submitAnnonce = async (event) => {
  event.preventDefault();
  console.log(event.target.input)
  var annonceObject = {};
  for (let i = 0; i < event.target.length; i++) {
    let element = event.target[i];
    let propTitle = element.name;

    if (propTitle !== 'image' || propTitle !== 'button') {
      annon
    }
  }
/*   const formData = await this.convertImagesToFormData();
  await this.uploadImagesToServer(formData) */
}

 onImageChange = (event) => {
  if(!event.target.files) return;   //target.files seçilen dosyaları döner

  const imageArray = [];  // create an array to store readable data of images
  // Array.from() target.files'ı iterable bir array'e dönüştürür
  Array.from(event.target.files).forEach(file => { 
    var reader = new FileReader();  //Yüklenen resmin datasını oku.
    reader.readAsDataURL(file);
    reader.addEventListener('load', () => { imageArray.push({name: file.name, data: reader.result})})
  })  

  this.setState({ imageArray})
};


 convertImagesToFormData =  async () => {
  var formData = new FormData();
  const imageArray = this.state.imageArray;

  for (const image of imageArray) {   //await kullanımı için for...of döngüsü
    const canvas = await getCroppedImage(image.data);
    const canvasDataUrl = canvas.toDataURL("image/jpeg");
    const convertedUrltoFile = dataURLtoFile(canvasDataUrl, image.name);

    formData.append("annonceImages", convertedUrltoFile);
    return formData;
  }
 }


 uploadImagesToServer = async (formData) => {
  instanceAxs.post('/newannonce/imageupload', formData)
    .then(result => {
      console.log(result)
      const resultData = result.data;
      if (resultData.message = 'images uploaded') {
        var imageLocations = resultData.files.map(e => e.location)
        this.setState({ imageLocations })
      }
    })
    .catch((err) => {
      console.log(err);
    });
 }


  render() {
    return (
      <div className="annonceFormDiv">
        <form id="annonce-form" onSubmit={this.submitAnnonce}>
          <input type="text" placeholder="Tittel" name="title" required></input>
          <input type="text" placeholder="Price" name="price" required></input>
          <input type="file" name="image" accept="image/*" multiple onChange={this.onImageChange} required></input>
          <button type="submit" name="button">Publish</button>
        </form>
      </div>
    );
  }
}

export default NewAnnonce;
