import React from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form, Row, Col, Container } from 'react-bootstrap';
import { GoogleLogin } from '@react-oauth/google';
import './Login.css';
import { instanceAxs } from "../../config/api.js";
import { useDispatch, useSelector } from "react-redux";
import { userActions } from "../../features/userSlice";
import { sendLoginRequest } from "../../features/userSliceActions";

const Login = () => { 
    
    const userState = useSelector(state => state.user.user);
    console.log(userState);

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleSubmit =  (event) => {
        event.preventDefault();
        const email = event.target[0].value;
        const password = event.target[1].value;
        dispatch(sendLoginRequest({email, password}))
/*         instanceAxs.post('/login', {email, password}).then(response => {
            console.log(response)
            const responseMessage = response.data.message;
            if (responseMessage !== 'user logged in')   return console.log(responseMessage);

            dispatch(userActions.login(response.data.user))
            navigate('/')

        }) */
    }

    const handleGoogleAuth = (credentialResponse) => {
        console.log(credentialResponse.credential)
        instanceAxs.post('/google/auth', {credential: credentialResponse.credential, email: 'example2@com', password: 'pass12'}).then(response => {
            console.log(response)
        }).catch(err => {
            console.log(err)
        })
    }
    
        return(
            <Container className="login-container" fluid  >
                <Row className="login__row" >
                    <Col lg={5} className="login__chat d-flex flex-direction-column align-items-center justify-content-center">
                        <div className="login-div" >
                        
                        <Form onSubmit={handleSubmit}  className="login-form">
                            

                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>Email address</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" name="email" required/>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" name="password" required/>
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <a href='/register' className="signup-link">Opprett ny konto</a>
                            </Form.Group>
                            <Form.Group className="form-submit-remember mb-3" controlId="formSubmitAndRemember">
                                <Button variant="primary" type="submit" className="">
                                    Logg Inn
                                </Button>
                                <Form.Check className="" type="checkbox" label="Husk meg neste gang" />

                            </Form.Group>
                            <hr/>
                            <GoogleLogin
                                // clientId="1011630835579-mkolirmm2rm5gqmk8bvvnv1r4449qdav.apps.googleusercontent.com"
                                onSuccess={handleGoogleAuth}
                                onFailure={(credentialResponse) => console.log('on fail')}
                                // cookiePolicy={"single_host_origin"}
                                >
                                <span>Sign Up with Google</span>
                                </GoogleLogin>
                                {/* <Button variant="primary" type="button" onClick={this.handleGoogleAuth}>Sign in wiht Google</Button> */}
                            </Form> 
                            </div>
                        </Col>
                        <Col lg={7}  className='login__bg '></Col>
                    </Row>
            </Container>
        )
    }


export default Login
