import './Register.css';

import { Link } from 'react-router-dom';
import { instanceAxs } from '../../config/api';


function Register() {

    //event handler olduğu için Register fonksiyonun içinde tanımlandı. 
    //başka bir tür fonnksiyon olsaydı ana fonksiyonun dışında tanımlanırdı.
    const onSubmit = (event) => {
        event.preventDefault();
        const email = event.target[0].value;
        const password = event.target[1].value;

        instanceAxs.post('/register', {email,password}).then(response => {
            const responseMessage = response.data.message;
            if (responseMessage !== 'user created') return console.log(responseMessage);
            this.props.history.push('/')
        })
    }


    return(
        <div className='registerDiv'>
            <form id="registerForm" onSubmit={onSubmit}>
                    <input type="email" placeholder="Email" required ></input>
                    <input type="password" placeholder="Password" required ></input>
                    <button type="submit">Register</button>
                    <Link to="/login">Login</Link>
            </form>
        </div>
    )
}

export default Register;
