import React from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form, Row, Col, Container, Spinner } from 'react-bootstrap';
import { GoogleLogin } from '@react-oauth/google';
import './Login.css';
import { useDispatch, useSelector } from "react-redux";
import { userActions } from "../../features/userSlice";
import { googleLoginRequest, sendLoginRequest } from "../../features/userSliceActions";

const Login = () => { 

    const dispatch = useDispatch();
    const isWaiting = useSelector(state => state.user.isWaiting)

    const handleSubmit =  (event) => {
        event.preventDefault();

        const email = event.target[0].value;
        const password = event.target[1].value;
        dispatch(sendLoginRequest({email, password}))
    }

    const handleGoogleAuth = (credentialResponse) => {
        console.log(credentialResponse.credential)
        dispatch(googleLoginRequest({credential: credentialResponse.credential, email: 'example@com', password: 'pass123'}))
    }
    
        return(
            <Container className="login-container" fluid  >
                <Row className="login__row" >
                    <Col lg={5} className="login__chat d-flex flex-direction-column align-items-center justify-content-center">
                        <div className="login-div" >
                        
                        <Form onSubmit={handleSubmit}  className="login-form">
                            

                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>Email address</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" name="email" required/>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" name="password" required/>
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <a href='/register' className="signup-link">Opprett ny konto</a>
                            </Form.Group>
                            <Form.Group className="form-submit-remember mb-3" controlId="formSubmitAndRemember">
                                {isWaiting ? 
                                <Button variant="primary">
                                    <Spinner></Spinner>
                                    Logger Inn...</Button>   
                                : 
                                <Button variant="primary" type="submit" className="">
                                Logg Inn
                            </Button>
                            }
                                <Form.Check className="" type="checkbox" label="Husk meg neste gang" />

                            </Form.Group>
                            <hr/>
                            <GoogleLogin
                                // clientId="1011630835579-mkolirmm2rm5gqmk8bvvnv1r4449qdav.apps.googleusercontent.com"
                                onSuccess={handleGoogleAuth}
                                onFailure={handleGoogleAuth}
                                // cookiePolicy={"single_host_origin"}
                                >
                                <span>Sign Up with Google</span>
                                </GoogleLogin>
                                {/* <Button variant="primary" type="button" onClick={this.handleGoogleAuth}>Sign in wiht Google</Button> */}
                            </Form> 
                            </div>
                        </Col>
                        <Col lg={7}  className='login__bg '></Col>
                    </Row>
            </Container>
        )
    }


export default Login
