import React, { useEffect, useState } from 'react'
import { InputGroup, Button, Form } from 'react-bootstrap'
import './Searchbar.css';
import { instanceAxs } from '../../config/api'

export default function Searchbar() {
    const [filteredData, setFilteredData] = useState([]);
    const [searchInput, setSearchInput] = useState('')

    const handleInput = (e) => {
        let input = e.target.value;
        setSearchInput(input)
    }

    const makeSearch =() => 

    useEffect(() => {
        if(searchInput === '') return setFilteredData([]);

        instanceAxs.post('/searchproduct', {q: searchInput}).then(response => {
            console.log(response)
            const responseData = response.data.productArray.map(item => {
                return item.title
            })
            setFilteredData(responseData)
        })
    }, [searchInput])

  return (
    <div>
        <div className='search-inputs'>
            <InputGroup>
                    <Form.Control className='search-inputs__input' type='text' placeholder='Søk...' onChange={handleInput} style={{width: 250}}></Form.Control>
            <Button style={{width: 80}}>Search</Button>
            </InputGroup>
        </div>
        {filteredData.length > 0 &&
        <div className='search-suggestions'>
            {filteredData.map((item, index) => {
                let link = '/search?q=' + item;
                return(
                    <a href={link} className='data-item' key={index}>
                        <p>{item}</p>
                    </a>
                )
            })}

        </div>
        }
    </div>
  )
}
