import { instanceAxs } from "../config/api";
import { userActions } from "./userSlice";

export const sendLoginRequest = (user) => {
    return async (dispatch) => {
        const handleRequest = async () => {
            instanceAxs.post('login', user).then(response => {

                const responseMsg = response.data.message;
                if(responseMsg === 'user logged in') {
                    const user = response.data.user;
                    window.localStorage.setItem('user', JSON.stringify(user));
                    window.localStorage.setItem('isLoggedIn', true)
                    dispatch(userActions.login(user))
                }

            }).catch(error => {
                console.log(error)
            })
        }
        await handleRequest();
    }
}

export const googleLoginRequest = (user) => {
    return async (dispatch) => {
        const handleRequest = async () => {
            instanceAxs.post('/google/auth', user).then(response => {

                const responseMsg = response.data.message;
                if(responseMsg === 'User logged in') {
                    const user = response.data.user;
                    window.localStorage.setItem('user', JSON.stringify(user));
                    window.localStorage.setItem('isLoggedIn', true)
                    dispatch(userActions.login(user))
                }

            }).catch(error => {
                console.log(error)
            })
        }
        await handleRequest();
    }
}

export const logoutRequest = () => {
    return async (dispatch) => {
        const handleRequest = async () => {
            instanceAxs.delete('/logout').then(response => {

                const responseMsg = response.data.message;
                if(responseMsg === 'user logged out') {
                    window.localStorage.removeItem('user');
                    window.localStorage.removeItem('isLoggedIn')
                    dispatch(userActions.logout())
                }

            }).catch(error => {
                console.log(error)
            })
        }
        await handleRequest();
    }
}
