import React, { useState } from 'react';
import data from './data/products.json';
import { Redirect } from 'react-router-dom';
import './App.css';


const ProductPage = ({match}) => {

    const product = data.find(product => {
        return match.params.id === product.id;
    })
    const [images,title,location,price,id] = [product.images, product.name,product.location,product.price,product.id];

    const [isHovered, setHovered] = useState(false);
    const [imgIndex, setImgIndex] = useState(0);
    const [currentImg, setImg] = useState(images[imgIndex]);

    function onHover(type) {
        setHovered(type === "true" ? true : false);
    }

    function changeImage(type){
        setImgIndex(type === "next" ? (imgIndex + 1) : (imgIndex -1 ));
        setImg(imgIndex);
    }
    
    return(
        <div style={{marginTop: 100}} className="container">
            {product !== undefined 
            ? <div className="container" id="frame">
                <div id="photo-frame" onMouseEnter={() => onHover("true")} onMouseLeave={() => onHover("false")}>
                    
                    <div className="big-photo-div">
                        {isHovered && <button className="btn btn-primary prev-button" onClick={() => changeImage("prev")}><i class="fas fa-chevron-left"></i></button>}
                        <img src={currentImg} alt="" id="product-big-image"></img>
                        {isHovered && <button className="btn btn-primary next-button" onClick={() => changeImage("next")}><i class="fas fa-chevron-right"></i></button>}
                        <p id="photo-desc"><span>very good very fdjsaşlf dsaş jsa (8/10)</span></p>
                    </div>

                    <div className="small-photo-div">
                        <ul>
                            {images.map((img,i) => {
                                return(
                                    <li >
                                        <img src={img} alt="" id="product-small-img" className={`border rounded m-2 ${img === currentImg ? "border-3 border-primary" : "border-1 border-secondary"}`}></img> 
                                    </li>
                                )
                            })}
                        
                        </ul>
                    </div>
                </div>
                <div id="saler-frame">
                    <p>{product.name}</p>
                    <ul>
                        <li>Ahmet</li>
                    </ul>
                </div> 
            </div>
                     
            : <Redirect to="/notfound"></Redirect>} 
        </div>
    )   
                 
}

export default ProductPage;
