import React from "react";
import { Link } from "react-router-dom";
import { instanceAxs } from "../../config/api";
/* import logo from "../../resources/logo.svg";
import { connect } from "react-redux"; */
import "./Navbar.css";


class Navbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentY: 0,
      isRender: true,
      searchInput: "a",
    };
    this.scroll = this.scroll.bind(this);
  }

  checkCharacters = async () => {
      let input = this.state.searchInput.trim();
      let searchInput = input.replace(/\s/g, "+");
      this.setState({
          searchInput
      })
  }

  makeSearch = async () => {
      await this.checkCharacters();
      let query = `/search?q=${this.state.searchInput}`;
      console.log("query",query)
      this.props.history.push( query);    
      window.location.reload(false)        
  }

  scroll() {
    if (window.scrollY > this.state.currentY) {
      this.setState({ isRender: false });
    } else {
      this.setState({ isRender: true });
    }
    this.setState({ currentY: window.scrollY });
  }

  logout = () => {
    instanceAxs.get('/logout').then(response => {
      console.log(response)
    })
  }

  render() {
    window.addEventListener("scroll", this.scroll);
    return (
      <div>
        {this.state.isRender &&
          <nav className="navbar navbar-expand-sm bg-dark" id="navbar" style={{marginBottom: 100}}>
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link" href="http://localhost:3000/">Finn</a>
              </li>
              <li className="nav-item">
                <input type="text" placeholder="Search..."></input>
                <button onClick={this.makeSearch}>Search</button>
              </li>
              <li className="nav-item">
                <p style={{color: 'white'}}>Ahmet Gokpinar</p>
              </li>
              <Link to='/profil'><button>Profil</button></Link>
              <Link to='/nyannonse'><button>Skap Annonse</button></Link>
              <Link to='/login'><button>Login</button></Link>
              <button onClick={this.logout}>Log out</button>
            </ul>
          </nav>
        }
      </div>
    );
  }
}

