import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { instanceAxs } from "../../config/api";
import "./Navbar.css";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useDispatch, useSelector } from "react-redux";
import { userActions } from "../../features/userSlice";

const Navigation = () => {
  const [currentY, setCurrentY] = useState(0);
  const [isRender, setIsRender] = useState(true);
  const [searchInput, setSearchInput] = useState('a');

  const isLoggedIn = useSelector(state => state.user.isLoggedIn)
  const email = useSelector(state => state.user.user.email);

  const dispatch = useDispatch();
  

  const checkCharacters = async () => {
      let input = searchInput.trim().replace(/\s/g, "+");
      setSearchInput(input)
      
  }

/*   const makeSearch = async () => {
      await checkCharacters();
      let query = `/search?q=${searchInput}`;
      console.log("query",query)
      navigate( query);    
      window.location.reload(false);
  } */

  const scroll = () => {
    if (window.scrollY > currentY) {
      setIsRender(false)
    } else {
      setIsRender(true)
    }
    setCurrentY( window.scrollY );
  }

  const logout = () => {
    instanceAxs.get('/logout').then(response => {
      console.log(response);
      dispatch(userActions.logout())
    })
  }

    window.addEventListener("scroll", scroll);
    return (
      <div>
        {isRender &&
          <Navbar bg="light" expand="lg">
          <Container>
            <Navbar.Brand href="/">Finn</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
            {isLoggedIn ? 
              <Nav className="me-auto">
                <NavDropdown title={email} id="basic-nav-dropdown">
                  <NavDropdown.Item href="/profil">Profil</NavDropdown.Item>
                  <NavDropdown.Item href="/nyannonse">
                    Skap Annonse
                  </NavDropdown.Item>
                  <NavDropdown.Item onClick={logout}>Log out</NavDropdown.Item>
                </NavDropdown>
              </Nav>
              :
              <Nav className="me-auto">
                
                <Nav.Link href="/login">Login</Nav.Link>
                <Nav.Link href="/register">Register</Nav.Link>
                
                
              </Nav>}
            </Navbar.Collapse>
          </Container>
        </Navbar>
        }
      </div>
    );
  
}

/* const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.isLogged,
    username: state.user.email,
  };
};
export default connect(mapStateToProps)(withRouter(Navbar)); */
export default Navigation;    // withRouther, history.push yapmak için gerekli
