import React, { useEffect, useState } from 'react';
import { useSelector } from "react-redux";
import './MyAnnonces.css';

import Breadcrumb from 'react-bootstrap/Breadcrumb';

import { instanceAxs } from '../../../config/api.js'
import ProductCard from '../../../Component/ProductCard/ProductCard.js';

const MyAnnonces = () => {

    const user = useSelector(state => state.user.user);
    const [annonceArray, setAnnonceArray] = useState(user.annonces)

    const getAnnonces = () => {
        instanceAxs.get('/userannonces').then(response => {
            console.log(response)
            const message = response.data.message;
            const productArray = response.data.productArray;

            if(message === 'Products found') {
                return this.setState({ productArray, isLoading: false })
            }
            console.log(message)
        })
        .catch(error => {
            console.log(error)
        })
    }



        return(
            <div className='myannonces-container'>
                <Breadcrumb>
                    <Breadcrumb.Item href='/min-konto'>Min konto</Breadcrumb.Item>
                    <Breadcrumb.Item href='/mine-annonser' active>Mine Annonser</Breadcrumb.Item>
                </Breadcrumb>
                <div className='myannonces-content'>
                    {(annonceArray && annonceArray.length)}
                </div>
            </div>
        )

}

export default MyAnnonces;
