import { createSlice } from '@reduxjs/toolkit'

const userSlice = createSlice({
    name: 'user',
    initialState: {},
    reducers: {
        login(state, action) {
            state = action.payload;
        },
        logout() {},
    }
})

export const userActions = userSlice.actions;

export default userSlice;
