import './Profile.css';
import React, { useEffect, useState } from 'react';
import { instanceAxs } from '../../../config/api.js';
import { getCroppedImage } from '../../../utils/cropImage';
import { dataURLtoFile } from '../../../utils/dataURltoFile';
import Breadcrumb from 'react-bootstrap/Breadcrumb';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Avatar from '@mui/material/Avatar';
import { useDispatch, useSelector } from 'react-redux';
import { updateUser } from '../../../features/userSliceActions';

const Profile = () => {

    const user = useSelector(state => state.user.user)
    const hiddenFileInput = React.useRef(null);
    const dispatch = useDispatch()

    const [isLoading, setIsLoading] = useState()
    const [imageFile, setImageFile] = useState(null);
    const [formData,setFormData] = useState(null)
    const [readerResult, setReaderResult] = useState(null);
    const [counties, setCounties] = useState();
    const [name, setName] = useState('')
    const [lastName, setLastName] = useState('');
    const [address, setAddress] = useState('');
    const [postnumber, setPostnumber] = useState(0);
    const [kommune, setKommune] = useState('undefined')


      const convertImagesToFormData =  async (data) => {
        var formData = new FormData();
        const canvas =  await getCroppedImage(data);
        const canvasDataUrl = canvas.toDataURL("image/jpeg");
        const convertedUrltoFile = dataURLtoFile(canvasDataUrl, "profilePicture.jpg");
        return new Promise(resolve => {
            formData.append("profileImage", convertedUrltoFile);
            resolve(formData)
        })
       }
       const convertToBase64 = (file) => {
        return new Promise(resolve => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                resolve({name: file.name, data: reader.result})
            }
        })
      }
      const handleFileChange = (e) => {
            const file = e.target.files[0]
            setImageFile(file)
    }
    const handleButtonClick = () => {
        hiddenFileInput.current.click()
    }
    const handlePostnumber = (e) => {
        if(!counties) return;
        let value = e.target.value;
        let commune = counties.find(item => item.kommuneNummer === value);
        if(commune) {
            setKommune(commune.kommuneNavn )
            setPostnumber(value)
        } else {
            setKommune('')
        }
    }

       const updateUserInfo = () => {
        const data = {
            name: name, 
            lastname: lastName, 
            address: address,
            postnumber: postnumber
        }
        setTimeout(() => {
            dispatch(updateUser(data))
        }, 1000)
       }

    useEffect(async () => {
        if(!imageFile) return;

        let file = imageFile;
        let convertedImage = await convertToBase64(file)
        setReaderResult(convertedImage.data)
        let formdata = await convertImagesToFormData(convertedImage.data)
        setFormData(formdata);
    }, [imageFile])

    useEffect(() => {
        console.log(user)
    }, [user])

    useEffect(() => {
        fetch('https://ws.geonorge.no/kommuneinfo/v1/fylkerkommuner', {method: 'GET'})
        .then(response => response.json())
        .then(data => {
            let kommuneArray = [];
            data.map(item => {
                item.kommuner.map(kommune => {
                    kommuneArray.push({fylke: kommune.fylkesnavn, kommuneNavn: kommune.kommunenavn, kommuneNummer: kommune.kommunenummer})
                })
            })
            setCounties(kommuneArray) 
          });       
    },[])


        return(
            <div className='profile-container'>

                        <Breadcrumb>
                            <Breadcrumb.Item href='/profil'>My profile</Breadcrumb.Item>
                            <Breadcrumb.Item href='/profile' active>Profile</Breadcrumb.Item>
                        </Breadcrumb>
      
                <div className='profile-content'>
                    <Row className='profile-content-row'>
                        <Col className='profile-content-col content-profileImage' lg={3}>
                            <div className='content-profileImage-div'>
                                <Avatar alt="avatar" src={user.profilePicture ? user.profilePicture : (readerResult || "")} sx={{width: 180, height: 180}}></Avatar>
                            </div>
                            <div className='between'>

                                <Form.Control type='file' accept="image/*" ref={hiddenFileInput} onChange={handleFileChange} style={{display: 'none'}}></Form.Control>
                            <Button className='avatar-control-buttons' variant='primary' onClick={handleButtonClick}>Endre</Button>
                            <Button className='avatar-control-buttons' variant='outline-danger'>Reset</Button>
                            </div>

                        </Col>

                        <Col lg={4} className='profile-content-col'>
                            <Form>
                                <Form.Group className='profile-form-element'>
                                    <Form.Label>Email</Form.Label>
                                    <Form.Control type="Email" value='gokpinarahmet20@gmail.com' disabled />
                             </Form.Group>
                                <Row className='profile-form-element'>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Navn</Form.Label>
                                            <Form.Control type="text" onChange={(e) => setName(e.target.value)} />
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Etternavn</Form.Label>
                                            <Form.Control type="text" onChange={(e) => setLastName(e.target.value)} />
                                        </Form.Group>
                                    </Col>
                                </Row>
                                <Form.Group className='profile-form-element'>
                                            <Form.Label>Addresse</Form.Label>
                                            <Form.Control type="text" onChange={(e) => setAddress(e.target.value)} />
                                </Form.Group>
                                <Row className='profile-form-element'>
                                    <Col>
                                        <Form.Group>    
                                            <Form.Label>Postnummer</Form.Label>
                                            <Form.Control type="text" onChange={handlePostnumber}/>
                                        </Form.Group>
                                    </Col>
                                    <Col className='d-flex align-items-end justify-content-center'>
                                        <p style={{margin: 0}}> {kommune} Kommune</p>
                                    </Col>

                                </Row>

                            </Form>
                            <div className='profile-control-buttons'>
                                        <Button className='control-button' variant='outline-primary'>Cancel</Button>
                                        <Button className="control-button" variant='primary' onClick={updateUserInfo}>Save</Button>
                                </div>
                        </Col>
                    </Row>

                </div>
            </div>         
        )
    
}

export default Profile;

