import './Profile.css';
import React from 'react';
import { instanceAxs } from '../../../config/api.js';
import { getCroppedImage } from '../../../utils/cropImage';
import { dataURLtoFile } from '../../../utils/dataURltoFile';
import Breadcrumb from 'react-bootstrap/Breadcrumb';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

class Profile extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            profileImgLink: '',
            profileImgBinary: 'null',
            isLoading: false
        }
    }

    uploadImage = async (event) => {
        event.preventDefault();

        const formData = await this.convertImagesToFormData();
        this.uploadImagesToServer(formData);
    }

    onImageChange = async (event) => {
        if(!event.target.files) return;   //target.files seçilen dosyaları döner
      
        const file = event.target.files[0];
        const convertedFile = await this.convertToBase64(file);
        this.setState({ profileImage: convertedFile });

/*         const imageArray = [];
        Array.from(event.target.files).forEach(file => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.addEventListener('load', () => imageArray.push({ name: file.name, data: reader.result }))
        })
        this.setState({ imageArray }) */
      };

      convertImagesToFormData =  async () => {
        // var formData = new FormData();
        var formData = new FormData();
        const image = this.state.profileImage;
        const canvas =  await getCroppedImage(image.data);
        const canvasDataUrl = canvas.toDataURL("image/jpeg");
        const convertedUrltoFile = dataURLtoFile(canvasDataUrl, image.name);
        return new Promise(resolve => {
     
            formData.append("profileImage", convertedUrltoFile);
            resolve(formData)
        })
/*         const imageArray = this.state.imageArray;
        for (const image of imageArray) {   //await kullanımı için for...of döngüsü
          const canvas = await getCroppedImage(image.data);
          const canvasDataUrl = canvas.toDataURL("image/jpeg");
          const convertedUrltoFile = dataURLtoFile(canvasDataUrl, image.name);
      
          formData.append("profileImage", convertedUrltoFile);
          return formData; 
        }*/
       }

       uploadImagesToServer =  (formData) => {
        instanceAxs.post('/profile/uploadprofileimg', formData).then(result => {
            if (result.data.message === 'images uploaded') {
              var imageLocations = result.data.files.map(e => e.location)
              this.setState({ imageLocations })
            }
          })
          .catch( err => console.log(err) );
       }

      convertToBase64 = (file) => {
        return new Promise(resolve => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                resolve({name: file.name, data: reader.result})
            }
        })
      }

    getProfileImage = () => {
        instanceAxs.get('/profile/getprofileimg').then(response => {
            console.log(response)
            this.setState({ isLoading: false, profileImgLink: response.data})
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        // this.getProfileImage();
    }

    render() {
        return(
            <div className='profile-container'>
                <div>
                <div>
                    <Breadcrumb>
                        <Breadcrumb.Item href='/profil'>My profile</Breadcrumb.Item>
                        <Breadcrumb.Item href='/profile' active>Profile</Breadcrumb.Item>
                    </Breadcrumb>
                </div>
                <div className='profile-content'>
                    <Row className='profile-content-row'>
                        <Col className='profile-content-col content-profileImage' lg={2}>
                            <img 
                                alt='pp' 
                                className='rounded-3 profile-image-div' 
                                src='https://images.unsplash.com/photo-1577563908411-5077b6dc7624?ixlib=rb-1.
                                2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1740&q=80'>
                            </img>
                            <Button variant='primary'>Endre profil bilde</Button>
                            <Button variant='danger'>Reset profil bilde</Button>
                        </Col>

                        <Col lg={4}>
                            <Form>
                                <Form.Group className='profile-form-element'>
                                    <Form.Label>Email</Form.Label>
                                    <Form.Control type="Email" value='gokpinarahmet20@gmail.com' disabled />
                             </Form.Group>
                                <Row className='profile-form-element'>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Navn</Form.Label>
                                            <Form.Control type="text" placeholder="name@example.com" />
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Etternavn</Form.Label>
                                            <Form.Control type="text" placeholder="name@example.com" />
                                        </Form.Group>
                                    </Col>
                                </Row>
                                <Form.Group className='profile-form-element'>
                                            <Form.Label>Addresse</Form.Label>
                                            <Form.Control type="text" placeholder="" />
                                </Form.Group>
                                <Row className='profile-form-element'>
                                    <Col>
                                        <Form.Group>    
                                            <Form.Label>Postnummer</Form.Label>
                                            <Form.Control type="text" placeholder="" />
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Fylke</Form.Label>
                                            <Form.Select>
                                                <option>Rogaland</option>
                                            </Form.Select>
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Kommune</Form.Label>
                                            <Form.Select>
                                                <option>Stavanger</option>
                                            </Form.Select>
                                        </Form.Group>
                                    </Col>
                                </Row>

                            </Form>
                            <div className='profile-control-buttons'>
                                        <Button className='control-button' variant='danger'>Cancel</Button>
                                        <Button className="control-button" variant='primary'>Save</Button>
                                </div>
                        </Col>
                    </Row>

                </div>
                </div>
            </div>         
        )
    }
}

export default Profile;

