import "./Favorites.css";
import ProductCard from "../../../Component/ProductCard/ProductCard";
import { instanceAxs } from "../../../config/api";
import React from "react";

class Favorites extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            productArray: []
        }
    }

    getAnnonces = () => {
        instanceAxs.get('/favorites/get').then(response => {
            console.log(response);
            if(response.data.message === "Products found"){
               return this.setState({ isLoading: false, productArray: response.data.favorites})
            }
            console.log(response.data.message)
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        this.getAnnonces();
    }

    render() {
        return(
            <div>
                {!this.state.isLoading ? 
                this.state.productArray.map(item => {
                    return(
                        <div key={item._id}>
                            <ProductCard 
                                img={item.annonceImages} 
                                price={item.price} 
                                name={item.title} 
                                id={item._id}
                                location={item.location}/> 
                        </div>                          
                ) 
                })
                :
                <p>as</p>
            }
            </div>
        )
    }
}

export default Favorites;
