import React, { useEffect, useState } from 'react';
import './FilterBadge.css'
import Badge from 'react-bootstrap/Badge';

const FilterBadge = ({searchParams, removeSelectedFilter, counties}) => {

  const [params, setParams] = useState([]);

    const handleOnClick = (e,item) => {
        e.preventDefault();
        let key = item.key;
        let value = item.value;

        if(key === 'kommune'){
          removeSelectedFilter(key, value)
          return;
        }
        removeSelectedFilter(key)
    } 

    const getParams = () => {   
      const paramsArray = [];
      for(const [key, value] of searchParams.entries()){
          if(key !== 'q'){
            if(key === 'fylke') {
              counties.find(item => item)
            }
            paramsArray.push({key: key, value: value})   
          }
      }
      setParams(paramsArray)    
    }

    useEffect(() => {
      getParams()
    }, [searchParams]);


  return (
    <div className='badge-container'>
        {params.length > 0 && params.map((item,index) => {
                let msg = item.key + ": " + item.value;
            return(
            <h5 key={index} style={{margin: 5}} onClick={(e) => handleOnClick(e,item)}>
              <Badge variant='secondary' >{msg}</Badge>
            </h5>
            )
        })
        }
    </div>
  )
}

export default FilterBadge;
