import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./NewProductCard.css"
import CardProperties from "./CardProperties";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Carousel from 'react-bootstrap/Carousel';

function ProductCard(props) {

  const [images, title, location, price, id] = [
    props.images,
    props.title || "no name",
    props.location || "no place",
    props.price || "0",
    props.id || null,
  ];

  const setHoveredToTrue = () => {
    setHovered(true)
  }
  const [isHovered, setHovered] = useState(false);
  const [imgIndex, setImgIndex] = useState(0);

  return(
    <Card style={{width: '18rem'}}>
      <Card.Body >
        <Carousel indicators={false} wrap={isHovered ? true : false} onMouseEnter={setHoveredToTrue} onMouseLeave={setHovered(false)}>
          {images.map(img => {
            return(
              <Carousel.Item className="carousel-item">
              <img
                className="carousel-img"
                src={img}
              />
            </Carousel.Item>
            )
          })}

        </Carousel>
        <Card.Title>{title}</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
  )
}

export default ProductCard;


