import React, { useEffect, useState } from "react";
import "./SearchResult.css";
import ProductCard from "../../Component/ProductCard/ProductCard.js";
import { instanceAxs } from "../../config/api";
import { useLocation } from "react-router-dom";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Accordion from 'react-bootstrap/Accordion';
import Filters from "./Filters";

const SearchResult = () => {

  const [productArray, setProductArray] = useState([]);
  const location = useLocation();
  
  const [filters, setFilters] = useState([{
    queryKey: 'q',
    queryValue:  new URLSearchParams(location.search).get("q"),
  }]);
  const urlParams = new URLSearchParams(location.search);


/*   const setSortingCategory = (value) => {
    var finalArray = this.state.items;

    if(value === "price_desc") {
      let priceDescArray = [].concat(finalArray)
     .sort((a, b) => a.price > b.price ? 1 : -1)
      finalArray = priceDescArray;
    } else if(value === "price_asc") {
      let priceAscArray = [].concat(finalArray)
    .sort((a, b) => a.price < b.price ? 1 : -1)
      finalArray = priceAscArray;
    } else if(value === "published") {
      let publishArray = [].concat(finalArray)
    .sort((a, b) => a.date < b.date ? 1 : -1)
      finalArray = publishArray;
    }

    this.setState({
      items: finalArray
    })
  }; */

/*   const getFiltersOnMount = () => {
    let urlParams = new URLSearchParams(window.location.search)

    var params = [];
    for (const [key, value] of urlParams) {
      var filter = {};

      switch(key){
        case 'q':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: undefined,
            tagValue: undefined,
          };
          params.push(filter)
          break;
        case 'price_max':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: "maxPrice",
            tagValue: `til ${value}`,
          };
          params.push(filter);
          break;
        case 'price_min':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: "minPrice",
            tagValue: `fra ${value}`,
          };
          params.push(filter);
          break;
        case 'status':
          if(value === "new") {
            filter = {
              queryKey: key,
              queryValue: value,
              tagKey: "statusNew",
              tagValue: "Nytt",
            };
          } else if(value === "used") {
            filter = {
              queryKey: key,
              queryValue: value,
              tagKey: "statusUsed",
              tagValue: "Brukt",
            };
          } 
          params.push(filter);
          break;
        case 'published':
          filter = {
            queryKey : key,
            queryValue: value,
            tagKey: key,
            tagValue: value
          }
          params.push(filter);
          break;
        default:
          
      }
    }
    this.setState({
      filters: params
    });
    return params;
  }; */

