      this.searchAlgorithm(this.state.filters) 
  }; */

/*   const returnQueryString = (filters) => {
    var queryString = "";
    for (const filter of filters) {
      queryString += `${filter.queryKey}=${filter.queryValue}&`;
    }
    if (queryString[queryString.length - 1] === "&")
      queryString = queryString.slice(0, queryString.length - 1);

    return queryString;
  } */

  const searchAlgorithm = (filters) => {
    
    let queryString = this.returnQueryString(filters);
    // let query = `/file/getmenuitems?${queryString}`;
    let query2 = '/searchproduct?q=e'
    instanceAxs
      .get(query2)
      .then((response) => {
        console.log("Search response", response.data);
        if (response.status === 200) {
          const productArray = response.data.productArray;
          this.setState({ productArray });
          window.history.pushState("page2", "seach made", `/search?${queryString}`);
        } else {
          console.log(response.data.message)
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    // searchAlgorithm(params)
  }, [])

    return (
          <Container fluid className="searchresult-container">
            <Row className="filters-row">
                  <Col lg={4} className="filters-column">
                        <Filters></Filters>
                  </Col>
            </Row>
            <Row className="products-row">
              <Col>

              </Col>
            </Row>
          </Container>
    );
  
}

export default SearchResult;
