import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./NewProductCard.css"
import CardProperties from "./CardProperties";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Carousel from 'react-bootstrap/Carousel';
import ButtonGroup from 'react-bootstrap/ButtonGroup';

function ProductCard(props) {

  const [images, title, location, price, id] = [
    props.images,
    props.title || "no name",
    props.location || "no place",
    props.price || "0",
    props.id || null,
  ];

  const setHoveredToTrue = () => {
    setHovered(true)
  }
  const setHoveredToFalse = () => {
    setHovered(false)
  }
  const [isHovered, setHovered] = useState(false);
  const [imgIndex, setImgIndex] = useState(0);

  return(
    <Card style={{width: '18rem'}}>
      <Card.Body style={{padding: 0, margin: 0}}>

          <Carousel indicators={false} wrap={isHovered ? true : false} onMouseEnter={setHoveredToTrue} onMouseLeave={setHoveredToFalse}>
                {images.map(img => {
                  return(
                    <Carousel.Item className="carousel-item">
                    <img
                      className="carousel-img"
                      src={img}
                    />
                  </Carousel.Item>
                  )
                })}
            </Carousel>
<div className="card-properties">
          <Card.Title className="card-properties__item item-title">{title}</Card.Title>
          <Card.Subtitle className="card-properties__item item-location ">{location}</Card.Subtitle>
          <Card.Text className="card-properties__item item-price">{price} nok</Card.Text>
          <Card.Text className="card-properties__item">
                Some text about the nature right here.
          </Card.Text>
          </div>
          <ButtonGroup aria-label="Basic example">
      <Button variant="btn btn-danger"><i class="fa-regular fa-heart"></i></Button>
      <Button variant="primary">Melding</Button>
      <Button variant="secondary">Del</Button>
    </ButtonGroup>
      </Card.Body>
    </Card>
  )
}

export default ProductCard;


