import axios from "axios";
import { useEffect, useState } from "react";

function UploadImage(){

    const [doc, setDoc] = useState();
    const [image, setImage] = useState(null);


    const upload = (e) => {
        e.preventDefault();

        const data = new FormData() 

        data.append('file', doc)

        console.warn(doc);

        let url = "http://localhost:3080/file/uploadimage";

        axios.post(url, data, { // receive two parameter endpoint url ,form data 

        })

        .then(res => { // then print response status

            console.warn(res);

        })
    }

    const getImage = () => {
        axios.get("http://localhost:3080/file?type=img&id=61a45dee1d898f49a3b1f2da")
            .then()
    }

    const onFileChange = event => {
        if(event.target.files && event.target.files.length > 0){
            const reader = new FileReader();
            reader.readAsDataURL(event.target.files[0]);
            reader.addEventListener('load', () => {
                console.log(reader.result);
                setImage(reader.result);
            })
        }
    };

    const createImage = (url) => {
        new Promise((resolve, reject) => {
            const image = new Image();
            image.addEventListener("load", () => resolve(image));
            image.addEventListener("error", (error) => reject(error));
            image.setAttribute("crossOrigin", "anonymous"); // needed to avoid cross-origin issues on CodeSandbox
            image.src = url;
        });
    }


    const getCroppedImg = async (imageSrc, pixelCrop, rotation = 0) => {
        const image = new Image();
            image.addEventListener("load", () => image);
            image.addEventListener("error", (error) => console.log(error));
            image.setAttribute("crossOrigin", "anonymous"); // needed to avoid cross-origin issues on CodeSandbox
            image.src = imageSrc;

        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");

        const width = Math.max(1, Math.floor(image.width));
        const height = Math.max(1, Math.floor(image.height));

        const maxSize = Math.max(width, height);
        const safeArea = 2 * ((maxSize / 2) * Math.sqrt(2));

        // set each dimensions to double largest dimension to allow for a safe area for the
        // image to rotate in without being clipped by canvas context
        canvas.width = safeArea;
        canvas.height = safeArea;

        // draw rotated image and store data.
        ctx.drawImage(
            image,
            safeArea ,
            safeArea 
        );

        const data = ctx.getImageData(0, 0, safeArea, safeArea);

        // paste generated rotate image with correct offsets for x,y crop values.
        ctx.putImageData(
            data,
            0 - safeArea / 2 + width * 0.5 ,
            0 - safeArea / 2 + height * 0.5 
        );

        // As Base64 string
        // return canvas.toDataURL("image/jpeg");
        return canvas;
    }

    const generateDownload = async (imageSrc, crop) => {
        if (!imageSrc) {
            return;
        }

        const canvas = await getCroppedImg(imageSrc, crop);

        canvas.toBlob(
            (blob) => {
                const previewUrl = window.URL.createObjectURL(blob);

                const anchor = document.createElement("a");
                anchor.download = "image.png";
                anchor.href = URL.createObjectURL(blob);
                anchor.click();

                window.URL.revokeObjectURL(previewUrl);
            },
            "image/png",
            0.66
        );
    };

    const onDownload = () => {
        generateDownload(image,0);
    }

//encType="multipart/form-data" action="/file/uploadimage" method="POST" multiple="multiple"
    return(
        <div>
            <div className="upload">
                <form onSubmit={upload}>
                    <div className="input-group mb-3">
                        <input className="form-control" accept="image/*" type="file" id="file" name="file"  onChange={onFileChange}></input>
                    </div>
                    <button type="submit" className="btn btn-primary">submit</button>
                    <button type="button" className="btn btn-danger" onClick={onDownload}>download</button>
                </form>
            </div>
            <hr/>
            <div className="show-images">
                <div>
                    <img src="http://localhost:3080/file?type=img&id=61a45dee1d898f49a3b1f2da" width="250" height="250" alt="pic"/>
                </div>
                <button type="button" className="btn btn-primary mt-3" onClick={getImage}>Show image</button>
            </div>
        </div>
    )
}

export default UploadImage;
