import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { instanceAxs } from "../../config/api.js";
import './ProductPage.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Carousel from 'react-bootstrap/Carousel';

function ProductPage() {

    let { annonceId } = useParams();
    const [annonce, setAnnonce] = useState('');
    const [isLoading, setLoading] = useState(true)

    const findProduct = async () => {
        await instanceAxs.get(`/product?id=${annonceId}`)
            .then(respond => {
                if (respond.data.message === 'An error occured while finding the product') {
                    return console.log(respond.data.message);
                }
                
                setAnnonce(respond.data.product);
                setLoading(false)
            })   
            .catch(err => {
                console.log(err);
            })   
    }

    useEffect(() => {
        findProduct();
        //eslint-disable-next-line
    }, [isLoading])

    return(
        <div>
            { annonce !== '' &&
                <Container >
                    <Row className="productPage-row">
                        <Col style={{border: '2px solid red'}} className="productPage-content">
                            <Carousel className="image-carousel" slide="false">
                                <Carousel.Item>
                                    <img 
                                        src="https://images.finncdn.no/dynamic/1600w/2023/3/vertical-0/13/1/294/694/321_2796740.jpg"
                                        alt="first slide"
                                        className="annonce-image"/>
                                        
                                    <Carousel.Caption>
                                        <p>first slide</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                                <Carousel.Item>
                                    <img 
                                        src="https://images.finncdn.no/dynamic/1280w/2023/3/vertical-0/14/0/294/896/230_721791559.jpg"
                                        alt="first slide"
                                        className="annonce-image"/>
                                        
                                    <Carousel.Caption>
                                        <p>second slide</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                            </Carousel>
                            <div className="price-and-controlbuttons">
                                <p>1000 kr</p>
                                <Button>Legg til Favoritt</Button>
                                <Button>Del</Button>
                            </div>
                            <div className="content-description">
                                <p>Description</p>

                            </div>
                            <div className="content-special-props"></div>
                            <p>Nokkelinfo</p>
                        </Col>
                        <Col lg={5} style={{border: '2px solid green'}}>

                        </Col>
                    </Row>
                </Container>
            }
        </div>
    )
}

export default ProductPage;
