import './Profile.css';
import React, { useEffect, useState } from 'react';
import { instanceAxs } from '../../../config/api.js';
import { getCroppedImage } from '../../../utils/cropImage';
import { dataURLtoFile } from '../../../utils/dataURltoFile';
import Breadcrumb from 'react-bootstrap/Breadcrumb';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Avatar from '@mui/material/Avatar';
import { useDispatch, useSelector } from 'react-redux';
import { updateUser, removeProfilePicture } from '../../../features/userSliceActions';
import Spinner from "react-bootstrap/Spinner";

const Profile = () => {

    const user = useSelector(state => state.user.user)
    const hiddenFileInput = React.useRef(null);
    const dispatch = useDispatch()

    const [isLoading, setIsLoading] = useState(false)
    const [imageFile, setImageFile] = useState('');
    const [formData,setFormData] = useState('')
    const [readerResult, setReaderResult] = useState('');
    const [counties, setCounties] = useState('');
    const [name, setName] = useState('')
    const [lastName, setLastName] = useState('');
    const [address, setAddress] = useState('');
    const [postnumber, setPostnumber] = useState('');
    const [profilePicture, setProfilePicture] = useState('')

      const convertImagesToFormData =  async (data) => {
        var formData = new FormData();
        const canvas =  await getCroppedImage(data);
        const canvasDataUrl = canvas.toDataURL("image/jpeg");
        const convertedUrltoFile = dataURLtoFile(canvasDataUrl, "profilePicture.jpg");
        return new Promise(resolve => {
            formData.append("profileImage", convertedUrltoFile);
            resolve(formData)
        })
       }

       const convertToBase64 = (file) => {
        return new Promise(resolve => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                resolve({name: file.name, data: reader.result})
            }
        })
      }

      const handleFileChange = (e) => {
            const file = e.target.files[0]
            setImageFile(file)
    }

    const handleButtonClick = () => {
        hiddenFileInput.current.click()
    }

    const handlePostnumber = (e) => {
        let value = e.target.value;
        setPostnumber(value)
    }

    const updateUserInfo = () => {
        const data = {
            userdata: {
                name: name, 
                lastname: lastName, 
                address: address,
                postnumber: postnumber
            },
            formData: formData
        }
        setIsLoading(true)
        setTimeout(() => {

            dispatch(updateUser(data))
            setIsLoading(false)
        }, 1000)
    }

    const handleCancel = () => {
        setName(user.name);
        setLastName(user.lastname);
        setAddress(user.address);
        setPostnumber(user.postnumber);
        setReaderResult(null)
    }

    const handleReset = (e) => {
        e.preventDefault();
        dispatch(removeProfilePicture());
    }

    useEffect(async () => {
        if(!imageFile) return;

        let file = imageFile;
        let convertedImage = await convertToBase64(file)
        setReaderResult(convertedImage.data)
        let formdata = await convertImagesToFormData(convertedImage.data)
        setFormData(formdata);
    }, [imageFile])

    useEffect(() => {
        setName(user.name);
        setLastName(user.lastname);
        setAddress(user.address);
        setPostnumber(user.postnumber);
        setProfilePicture(user.profilePicture)
    }, [user])

    useEffect(async () => {
        await fetch('https://ws.geonorge.no/kommuneinfo/v1/fylkerkommuner', {method: 'GET'})
        .then(response => response.json())
        .then(data => {
            let kommuneArray = [];
            data.map(item => {
                item.kommuner.map(kommune => {
                    kommuneArray.push({fylke: kommune.fylkesnavn, kommuneNavn: kommune.kommunenavn, kommuneNummer: kommune.kommunenummer})
                })
            })
            setCounties(kommuneArray) 
          });       
    },[user])


        return(
            <div className='profile-container'>

                        <Breadcrumb>
                            <Breadcrumb.Item href='/profil'>My profile</Breadcrumb.Item>
                            <Breadcrumb.Item href='/profile' active>Profile</Breadcrumb.Item>
                        </Breadcrumb>
      
                <div className='profile-content'>
                    <Row className='profile-content-row'>
                        <Col className='profile-content-col content-profileImage' lg={3}>
                            <div className='content-profileImage-div'>
                                <Avatar alt="avatar" src={readerResult !== '' ? readerResult : (profilePicture !== '' ? profilePicture : "")} sx={{width: 180, height: 180}}></Avatar>
                            </div>
                            <div className='between'>

                            <Button className='avatar-control-buttons' variant='primary' onClick={handleButtonClick}>Endre</Button>
                            <Button className='avatar-control-buttons' variant='outline-danger' onClick={(e) => handleReset(e)}>Reset</Button>
                            </div>

                        </Col>

                        <Col lg={4} className='profile-content-col'>
                            <Form>
                                <Form.Group className='profile-form-element'>
                                    <Form.Label>Email</Form.Label>
                                    <Form.Control type="Email" value='gokpinarahmet20@gmail.com' disabled />
                             </Form.Group>
                                <Row className='profile-form-element'>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Navn</Form.Label>
                                            <Form.Control type="text" value={name} onChange={(e) => setName(e.target.value)} />
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group>
                                            <Form.Label>Etternavn</Form.Label>
                                            <Form.Control type="text" value={lastName} onChange={(e) => setLastName(e.target.value)} />
                                        </Form.Group>
                                    </Col>
                                </Row>
                                <Form.Group className='profile-form-element'>
                                            <Form.Label>Addresse</Form.Label>
                                            <Form.Control type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
                                </Form.Group>
                                <Row className='profile-form-element'>
                                    <Col>
                                        <Form.Group>    
                                            <Form.Label>Postnummer</Form.Label>
                                            <Form.Control type="text" value={postnumber} onChange={handlePostnumber}/>
                                        </Form.Group>
                                    </Col>
                                    <Col className='d-flex align-items-end justify-content-center'>
                                        <p style={{margin: 0}}> {(postnumber && counties !== '' && counties.find(item => item.kommuneNummer === postnumber)) &&  counties.find(item => item.kommuneNummer === postnumber).kommuneNavn} Kommune</p>
                                    </Col>

                                </Row>

                            </Form>
                            <div className='profile-control-buttons'>
                                        <Button className='control-button' variant='outline-primary' onClick={handleCancel}>Cancel</Button>
                                        {isLoading ? 
                                        <Button className='control-button'>
                                            <Spinner animation='border' size='sm' className='me-3'/> 
                                            Saving...
                                        </Button>   
                                    :
                                        <Button 
                                            className="control-button" 
                                            variant='primary' 
                                            onClick={updateUserInfo}
                                        >Save</Button>}
                                </div>
                        </Col>
                    </Row>

                </div>
            </div>         
        )
    
}

export default Profile;

/*                 <div className='profileContainer'>
                    <img src='http://localhost:3080/profile/getprofileimg?image_name=profilePicture.jpeg' alt="pp" width="300" height="300"/>
                    <input className='form-control w-25' type="file" name="image" accept="image/*"  onChange={this.onImageChange} required></input>
                    <button className='btn btn-primary' onClick={this.uploadImage}>Upload</button>  
                    <button className='btn btn-danger'>Reset</button>  
                </div> */
