import React, { useState } from "react";
import "./Navbar.css";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { logoutRequest } from "../../features/userSliceActions";
import Avatar from '@mui/material/Avatar';

const Navigation = () => {
  const [currentY, setCurrentY] = useState(0);
  const [isRender, setIsRender] = useState(true);
  const [searchInput, setSearchInput] = useState('a');
  const navigate = useNavigate();
  const isLoggedIn = useSelector(state => state.user.isLoggedIn)
  const email = useSelector(state => state.user.user.email);
  const user = useSelector(state => state.user.user);

  const dispatch = useDispatch();
  

  const checkCharacters = async () => {
      let input = searchInput.trim().replace(/\s/g, "+");
      setSearchInput(input) 
  }

const makeSearch = async () => {
      await checkCharacters();
      let query = `/search?q=${searchInput}`;
      console.log("query",query)
      navigate( query);    
      window.location.reload(false);
  }

  const scroll = () => {
    if (window.scrollY > currentY) {
      setIsRender(false)
    } else {
      setIsRender(true)
    }
    setCurrentY( window.scrollY );
  }

  const logout = () => {
    dispatch(logoutRequest())
  }

    window.addEventListener("scroll", scroll);
    return (
      <div>
        {isRender &&
          <Navbar bg="light" expand="lg" className="d-flex align-items-center justify-content-space-between">
          <Container>
            <Navbar.Brand href="/">Finn</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
            {isLoggedIn ? 
              <Nav className="me-auto d-flex align-items-center">
                <Avatar src={user.profilePicture} alt="pp"  sx={{ width: 32, height: 32 }}/>
                <NavDropdown title={email} id="basic-nav-dropdown" >
                  <NavDropdown.Item href="/profil">Profil</NavDropdown.Item>
                  <NavDropdown.Item href="/nyannonse">
                    Skap Annonse
                  </NavDropdown.Item>
                  <NavDropdown.Item onClick={logout}>Log out</NavDropdown.Item>
                </NavDropdown>
              </Nav>
              :
              <Nav className="me-auto">
                
                <Nav.Link href="/login">Login</Nav.Link>
                <Nav.Link href="/register">Register</Nav.Link>
              </Nav>}
            </Navbar.Collapse>
          </Container>
        </Navbar>
        }
      </div>
    );
  
}

