import React, { useState } from 'react';
import data from './data/products.json';
import { Redirect } from 'react-router-dom';
import './App.css';


const ProductPage = ({match}) => {

    const product = data.find(product => {
        return match.params.id === product.id;
    })
    const [images,title,location,price,id] = [product.img, product.name,product.location,product.price,product.id];

    const [isHovered, setHovered] = useState(false);
    const [imgIndex, setImgIndex] = useState(0);

    function onHover(type) {
        setHovered(type === "true" ? true : false);
    }
    
    return(
        <div style={{marginTop: 100}} className="container">
            {product !== undefined 
            ? <div className="container" id="frame">
                <div id="photo-frame" onMouseEnter={() => onHover("true")} onMouseLeave={() => onHover("false")}>
                    <div className="big-photo-div">
                        <img src={product.images[1]} alt="" id="product-big-image"></img>
                        {isHovered === true && <span>
                        <button className="btn btn-primary prev-button"><i class="fas fa-chevron-left"></i></button> 
                        <button className="btn btn-primary next-button"><i class="fas fa-chevron-right"></i></button>

                        </span>}

                        <p id="photo-desc"><span>very good very fdjsaşlf dsaş jsa (8/10)</span></p>
                    </div>
                    <div className="small-photo-div">
                        <ul>
                            {product.images.map((img,i) => {
                                return(
                                    <li>
                                        <img src={img} alt="" id="product-small-img"></img> 
                                    </li>
                                )
                            })}
                        
                        </ul>
                    </div>
                </div>
                <div id="saler-frame">
                    <p>{product.name}</p>
                    <ul>
                        <li>Ahmet</li>
                    </ul>
                </div> 
            </div>
                     
            : <Redirect to="/notfound"></Redirect>} 
        </div>
    )   
                 
}

export default ProductPage;
