import React, { useState } from "react";
import { Link } from "react-router-dom";
import { instanceAxs } from "../../config/api";
/* import logo from "../../resources/logo.svg";
import { connect } from "react-redux"; */
import "./Navbar.css";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';


const Navigation = () => {
  const [currentY, setCurrentY] = useState(0);
  const [isRender, setIsRender] = useState(true);
  const [searchInput, setSearchInput] = useState('a');

  const checkCharacters = async () => {
      let input = searchInput.trim().replace(/\s/g, "+");
      setSearchInput(input)
      
  }

  const makeSearch = async () => {
      await checkCharacters();
      let query = `/search?q=${searchInput}`;
      console.log("query",query)
/*       this.props.history.push( query);    
      window.location.reload(false)   */      
  }

  const scroll = () => {
    if (window.scrollY > currentY) {
      setIsRender(false)
    } else {
      setIsRender(true)
    }
    this.setState({ currentY: window.scrollY });
  }

  const logout = () => {
    instanceAxs.get('/logout').then(response => {
      console.log(response)
    })
  }

    window.addEventListener("scroll", this.scroll);
    return (
      <div>
        {this.state.isRender &&
          <nav className="navbar navbar-expand-sm bg-dark" id="navbar">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link" href="http://localhost:3000/">Finn</a>
              </li>
              <li className="nav-item">
                <input type="text" placeholder="Search..."></input>
                <button onClick={this.makeSearch}>Search</button>
              </li>
              <li className="nav-item">
                <p style={{color: 'white'}}>Ahmet Gokpinar</p>
              </li>
              <Link to='/profil'><button>Profil</button></Link>
              <Link to='/nyannonse'><button>Skap Annonse</button></Link>
              <Link to='/login'><button>Login</button></Link>
              <button onClick={this.logout}>Log out</button>
            </ul>
          </nav>
        }
      </div>
    );
  
}

