import "./Favorites.css";
import ProductCard from "../../../Component/ProductCard/ProductCard";
import { instanceAxs } from "../../../config/api";
import React from "react";

class Favorites extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            productArray: []
        }
    }

    getAnnonces = () => {
        instanceAxs.get('/favorites/get').then(response => {
            console.log(response);
            this.setState({ isLoading: false, productArray: this.state.favorites})
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        this.getAnnonces();
    }

    render() {
        return(
            <div className="">
                    { (this.state.isLoading === false) ? 
                    
                    (this.state.productArray.map(item => {
                        var annonce = item;
                        console.log(annonce._id)
                        return(
                                <div key={annonce._id}>
                                    <ProductCard 
                                        img={annonce.annonceImages} 
                                        price={annonce.price} 
                                        name={annonce.title} 
                                        id={annonce._id}
                                        location={annonce.location}/> 
                                </div>                          
                        ) 
                    }))
                    : 
                    <p>loading</p>
                }                                                      
            </div>  
        )
    }
}

export default Favorites;
