import React, { useEffect, useState } from 'react'
import { Dropdown, InputGroup } from 'react-bootstrap';
import Accordion from 'react-bootstrap/Accordion';
import Form from 'react-bootstrap/Form';
import './Filters.css'
import Button from 'react-bootstrap/Button'

//location, main cat, sub cat, date, old-new, price
const Filters = ({handleFilterChange, removeSelectedFilter, searchParams, counties}) => {

    const [communes, setCommunes] = useState([]);
    const [state, setState] = useState('');
    const [city, setCity] = useState(['']);
    const [mainCategory, setMainCategory] = useState('');
    const [subCategory, setSubCategory] = useState('');
    const [minPrice, setMinPrice] = useState('');
    const [maxPrice, setMaxPrice] = useState('');
    const [productDate, setProductDate] = useState();
    const [productStatus, setProductStatus] = useState(false);
    const [filter, setFilter] = useState([{}])

    const handleChanges = (key, value) => {
        handleFilterChange(key, value)
    }

    const findCounty = (value) => {
        if(!counties) {
            return null;
        }
        const county = counties.find(item => item.fylkesnummer === value);
        return county;
    }

    const handleStateChange = (e) => {
        let value = e.target.value;
        if(value === ''){
            removeSelectedFilter('fylke')
            return;
        }
        const county = findCounty(value);
        setCommunes(county !== null && county.kommuner)
        handleChanges('fylke', value);
    }

    const handleCityChange = (e) => {
        if(e.target.checked === true){
            handleChanges('kommune', e.target.value)
        }  else {
            removeSelectedFilter('kommune', e.target.value)
        }
    }

    const handleMinPriceChange = (e) => {
        setMinPrice(e.target.value)
    }
    const handleMaxPriceChange = (e) => {
        setMaxPrice(e.target.value)
    }
    const handlePriceSubmit = (e) => {
        if (minPrice !== '') {
            handleChanges('min_price', minPrice)
        } 
        if (maxPrice !== ''){
            handleChanges('max_price', maxPrice)
        }
    }

    const handleDateChange = (e) => {
        console.log(e.target.value);
        setProductDate(e.target.value);
        handleChanges('date', e.target.value)
    }

    useEffect(() => {
            let fylke = searchParams.get('fylke');
            if(fylke) {
                setState(fylke);
                const county = findCounty(fylke);
                setCommunes(county !== null && county.kommuner);
            } else {
                setState('');
                setCommunes([])
            }

            let kommune = searchParams.getAll('kommune');
            setCity(kommune === null ? [] : kommune)
    }, [searchParams, counties])

  return (
    <div>
      <Accordion className="filter-accordion">
        <Accordion.Item eventKey="1">
          <Accordion.Header>Location</Accordion.Header>
          <Accordion.Body>
            <Form.Select
              aria-label="state-selection"
              className="mb-2"
              onChange={handleStateChange}
              value={state}
            >
              <option value="">Valg en Fylke</option>
              {counties.length > 0 &&
                counties.map((county) => {
                  return (
                    <option
                      key={county.fylkesnummer}
                      value={county.fylkesnummer}
                      itemID={county.fylkesnavn}
                    >
                      {county.fylkesnavn}
                    </option>
                  );
                })}
            </Form.Select>
            {communes.length > 0 && (
              <div className="commune-filter">
                {communes.map((kommune) => {
                  return (
                    <Form.Check
                      onChange={handleCityChange}
                      checked={
                        city.find(
                          (item) => item === kommune.kommunenavnNorsk
                        ) || false
                      }
                      key={kommune.kommunenummer}
                      label={kommune.kommunenavn}
                      value={kommune.kommunenavn}
                      name={kommune.kommunenavn}
                    ></Form.Check>
                  );
                })}
              </div>
            )}
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>

      <Accordion className="filter-accordion">
        <Accordion.Item eventKey="2">
          <Accordion.Header>Category</Accordion.Header>
          <Accordion.Body>
            <Form.Select aria-label="maincategory-selection" className="mb-2">
              <option>Select a category</option>
              <option value="bil">Bil</option>
              <option value="bolig">Bolig</option>
            </Form.Select>
            <Form.Select aria-label="subcategory-selection">
              <option>Select a sub category</option>
              <option value="elbil">Elbil</option>
              <option value="enebolig">Enebolig</option>
            </Form.Select>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>

      <Accordion className="filter-accordion">
        <Accordion.Item eventKey="3">
          <Accordion.Header>Pris</Accordion.Header>
          <Accordion.Body>
            <InputGroup className="mb-3">
              <Form.Control
                type="number"
                placeholder="min. pris"
                onChange={handleMinPriceChange}
              ></Form.Control>
              <InputGroup.Text id="basic-addon2">kr</InputGroup.Text>
            </InputGroup>
            <InputGroup className="mb-3">
              <Form.Control
                type="number"
                placeholder="max. pris"
                onChange={handleMaxPriceChange}
              ></Form.Control>
              <InputGroup.Text id="basic-addon2">kr</InputGroup.Text>
            </InputGroup>
            <Button variant="primary" type='button' onClick={handlePriceSubmit}>Search</Button>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>

      <Accordion className="filter-accordion">
        <Accordion.Item>
          <Accordion.Header>Date</Accordion.Header>
          <Accordion.Body>
            <Form onChange={handleDateChange}>
              <Form.Check
                type="radio"
                name="date"
                label="Siste 24 timer"
                value="today"
              />
              <Form.Check
                type="radio"
                name="date"
                label="I uke"
                value="this week"
              />
              <Form.Check
                type="radio"
                name="date"
                label="I month"
                value="this month"
              />
            </Form>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>

      <Accordion className="filter-accordion">
        <Accordion.Item>
          <Accordion.Header>Status</Accordion.Header>
          <Accordion.Body>
            <Form>
              <Form.Check type="radio" name="status" label="Nytt"></Form.Check>
              <Form.Check type="radio" name="status" label="Brukt"></Form.Check>
            </Form>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>
    </div>
  );
}

export default Filters;
