import "./Favorites.css";
import ProductCard from "../../../Component/ProductCard/ProductCard";
import { instanceAxs } from "../../../config/api";
import React from "react";

const getAnnonces = () => {
    instanceAxs.get('/favorites/get').then(response => {
        return response.data.favorites;
    })
}

class Favorites extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            productArray: []
        }
    }

    componentDidMount() {
        let productArray = getAnnonces();
        this.setState({ isLoading: false, productArray })
    }

    render() {
        return(
            <div className="container HomePageContainer">
                <div className="homePageItems">  
                    { (this.state.isLoading === false) ? 
                    
                    (this.state.productArray.map(item => {
                        var annonce = item;
                        console.log(annonce._id)
                        return(
                                <div key={annonce._id}>
                                    <ProductCard 
                                        img={annonce.annonceImages} 
                                        price={annonce.price} 
                                        name={annonce.title} 
                                        id={annonce._id}
                                        location={annonce.location}/> 
                                </div>                          
                        ) 
                    }))
                    : 
                    <p>loading</p>
                }                                                      
                </div>
            </div>  
        )
    }
}

export default Favorites;
