import { useEffect, useState } from "react";
import { useParams } from "react-router";
import CarAnnonce from "./components/CarAnnonce";
import PropertyAnnonce from "./components/PropertyAnnonce";
import PhotoAndDescription from "./Media";
import Spinner from "../../Component/Spinner/Spinner";
import axios from "axios";
import { instanceAxs } from '../../config/api.js';
import { getCroppedImage } from "../../utils/cropImage";
import { dataURLtoFile } from "../../utils/dataURltoFile";
import './CreateAnnonce.css'

function CreateAnnonce(props) {
  var { category } = useParams();
  // let {annonceId} = useParams();
  const [CategoryProperties, setComponent] = useState(<></>);
  const [isLoading, setLoading] = useState(true);
  const [properties, setProperties] = useState({ uniqueProps: {} });
  const [images, setImages] = useState();
  const [imageLocations, setImageLocations] = useState();

  function findCategory() {
    if (isLoading) {
      switch (category) {
        case "bil":
          setComponent(<CarAnnonce></CarAnnonce>);
          setLoading(false);
          break;
        case "bolig":
          setComponent(<PropertyAnnonce></PropertyAnnonce>);
          setLoading(false);
          break;
        default:
          setComponent(<p>Annonce type couldn't found!</p>);
          setLoading(false);
          break;
      }
    }
  }

  const handleImageChange = (imgArray) => {
    setImages(imgArray);
      console.log(imgArray);
  };

  async function convertImagesToFormData (selectedFiles) {
    var formData = new FormData();

    for (let i = 0; i < selectedFiles.length; i++) {
      var img = selectedFiles[i];

      const canvas = await getCroppedImage(img);
      const canvasDataUrl = canvas.toDataURL("image/jpeg");
      const convertedUrltoFile = dataURLtoFile(
        canvasDataUrl,
        `${Date.now()}-myimage.jpg`
      );
      console.log(convertedUrltoFile);

      formData.append("annonceImages", convertedUrltoFile);
      return formData;
    }

  }

  async function uploadImagesToServer (formData) {
    instanceAxs.post('/newannonce/imageupload', formData)
    .then(result => {
      console.log(result)
      var imageLocations = result.data.files.map(e => e.location)
      setImageLocations(imageLocations);
    })
    .catch((err) => {
      console.log(err);
    });
  }

  async function uploadAnnonceToServer () {
    var annonce = { properties, imageLocations };
    console.log(annonce);
    instanceAxs.post('/newannonce/create', annonce)
    .then(result => {
      console.log(result)
    })
    .catch((err) => {
      console.log(err);
    });
  }

  const submitAnnonce = async () => {
    let selectedFiles = images;
     var formData = await convertImagesToFormData(selectedFiles) // create form to data to upload images properly

    await uploadImagesToServer(formData)

  };

  useEffect(() => {
    findCategory();
   });

  return (
    <div>
      {!isLoading ? (
        <div className="createAnnonceDiv">
            <h4>{category}</h4>
            <input type="text" placeholder="Tittel..."/>
            <input type="text" placeholder="Price..."/>

            {CategoryProperties}

          <PhotoAndDescription 
            onchange={handleImageChange}
          ></PhotoAndDescription>

          <button type="button"onClick={submitAnnonce}>Publish</button>
        </div>
      ) : (
        <Spinner />
      )}
    </div>
  );
}

export default CreateAnnonce;
