import React from 'react';
import './FilterBadge.css'
import Badge from 'react-bootstrap/Badge';

const FilterBadge = ({filters, removeSelectedFilter}) => {
    const handleOnClick = (e,key) => {
        e.preventDefault()
        console.log(key)
        //removeSelectedFilter(key)
    } 
  return (
    <div className='badge-container'>
        {filters.length > 0 && filters.map((item,index) => {
            if(item.queryKey !== 'q'){
                let msg = item.queryKey + ": " + item.queryValue;
            return(
            <h5 key={index} style={{margin: 5}} onClick={(e) => handleOnClick(e,item.queryKey)}>
              <Badge variant='secondary' >{msg}</Badge>
            </h5>
            )
            }
        })

        }

    </div>
  )
}

export default FilterBadge;
