import React, { useEffect, useState } from "react";
import { Switch, Route, Link, useRouteMatch } from "react-router-dom";
import './CreateAnnonce.css';

import { instanceAxs } from "../../api/Api";


function CreateAnnonce() {

    let { path, url } = useRouteMatch();

    var house = {
        name: "Enebolig",
        id: 4325,
        subs: [
            {title : "kiralik", desc: "kiralik ev"},
            {title : "satilik", desc: "satilik ev"},
            {title : "komple", desc: "komple ev"},
        ]
    }
    var car = {
        name: "Bil",
        id: 9876,
        subs: [
            {title : "benzinli", desc: "benzinli araba"},
            {title : "dizel", desc: "dizel araba"},
        ]
    }
    const categories = [
        house, car
    ]

    const [category, setCategory] = useState({name: '', id: 0, subs: [{}]});
    const [subCategory, setSubCategory] = useState({title: '', desc: ''});
    const [currentSubs, setCurrentSubs] = useState([]);
    const [newAnnonce, setNewAnnonce] = useState({});

    useEffect(() => {
        if(category !== undefined){
            setCurrentSubs(category.subs);
            setNewAnnonce({ category: category.name, subCategory: subCategory.title});
        }
    }, [category, subCategory])

    const changeCategory = (category) => {
        setCategory(category)
    }

    const changeSubCategory = (subcat) => {
        setSubCategory(subcat);
    }

    const create = () => {
        instanceAxs.post('/newannonce', newAnnonce)
            .then(result => {
                console.log(result)
            })
            .catch(error => {
                console.log(error)
            })
    }

    return(
        <div >
            <Switch>
                <Route exact path={path}>
                    <div className="createAnnonceMainDiv">
                        <div className="chooseMainCategoryDiv">
                        { categories.map(cat => (
                            <button type="button" className="btn border border-secondary" name={cat.name} value={cat} onClick={() => changeCategory(cat)}>{cat.name}</button>
                        ))}
                    </div>
                    <div className="subCategoryDiv">
                        {currentSubs.length > 0 ? 
                            <select className="form-select" >
                                <option defaultValue value="publishedNew">Choose a sub cat</option>
                                    {currentSubs.map(sub => (
                                        <option key={sub.title} value={sub.title} onClick={() => changeSubCategory(sub)}>{sub.title}</option>
                                    ))}         
                            </select>
                            :
                            <select className="form-select">
                                <option defaultValue value="publishedNew">Choose a main cat</option>
                            </select>              

                        }
                    </div>
                    <Link to={`${url + '/ny/:categoryId'}`}><button className="btn btn-primary">Lagre & Gå Videre</button></Link>
                    </div>
                </Route>
                <Route path={`${path + '/ny/:categoryId'}`}>

                </Route>
                </Switch>

         </div>
    )

}

export default CreateAnnonce;
