import React from "react";
import { Link } from "react-router-dom";
import { Button, Form, Row, Col, Container } from 'react-bootstrap'
import './Login.css';
import { instanceAxs } from "../../config/api.js";

class Login extends React.Component{ 
    constructor(props){
        super(props);
        this.state = {
            user: {
                email: '',
                password: ''
            },
        }
        // this.onSubmit = this.onSubmit.bind(this);
    } 

    handleSubmit =  (event) => {
        event.preventDefault();
        const email = event.target[0].value;
        const password = event.target[1].value;

        instanceAxs.post('/login', {email, password}).then(response => {
            const responseMessage = response.data.message;
            if (responseMessage !== 'user logged in')   return console.log(responseMessage);

            this.props.history.push('/')
        })
        // this.props.login(this.state.user);   
    }
    
    render(){
        return(
            <Container className="mt-5">
                <Row>
                    <Col md={7} xs={12} className="d-flex align-items-center justify-content-center flex-direction-column">
                        <div className="login-div border rounded" style={{ width: "80%", maxWidth: 450 }}>
                        <h3 className="form-title">Logg Inn</h3>
                        
                        <Form onSubmit={this.handleSubmit}  className="login-form">
                            

                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>Email address</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" name="email" required/>
                                <Form.Text className="text-muted">
                                We'll never share your email with anyone else.
                                </Form.Text>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" name="password" required/>
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicCheckbox">
                                <Form.Check type="checkbox" label="Check me out" />
                            </Form.Group>
                            <Button variant="primary w-100" type="submit">
                                Submit
                            </Button>
                            </Form>
                            </div>
                        </Col>
                        <Col md={5} className='login__bg '></Col>
                    </Row>
            </Container>
        )
    }
}

export default Login
