import React from 'react';
import './style.css';
import data from './data/products.json';



class Product extends React.Component{

    constructor(props) {
        super(props); 
        this.state = {
          isHovered: false,
          imgIndex: 0,
        };
      }
      handleEnter() {
        this.setState({
          isHovered: true 
        });
      }
    
      handleLeave() {
        this.setState({
          isHovered: false 
        });
      }
    

    render(){  
      var images = this.props.img; 
      const productData = data.map( (data) => {

      
      return(
          <div className="card h-100 border-2 shadow">
            <div className="card-img" 
              onMouseEnter={this.handleEnter.bind(this)}
              onMouseLeave={this.handleLeave.bind(this)} >             
              <img src={images} className="img-responsive w-100" alt="alt-image-cap" id="product-photo"/>
                {this.state.isHovered && (
                    <div className="d-flex card-img-overlay justify-content-between" id="">
                    <button className="btn btn-light rounded-circle" id="left-arrow" ><i className="fas fa-arrow-left"></i></button>
                    <button className="btn btn-light rounded-circle align-self-center" id="right-arrow" ><i className="fas fa-arrow-right"></i></button>
                </div>
                )}
            </div>

            <div className="card-body d-flex flex-column">
              <div className="d-flex mb-2">
                <h5 className="card-title" id="pris">6000 kr</h5>
                <p className="card-text " id="pris">&middot;</p>
                <p className="card-text " id="pris">Bergen</p>
                <div className="ms-auto">
                  <button className="btn btn-primary me-2" data-toggle="tooltip" title="Send Message"><i className="fas fa-envelope"></i></button>
                  <button type="button" className="btn btn-outline-danger" data-toggle="tooltip" title="Add to Favorites"><i className="fas fa-heart"></i></button>
                </div>
              </div>
                
              <p className="card-text">
                {this.props.desc != null 
                ? (this.props.desc.length > 95 ? this.props.desc.substring(0,97)+'...' : this.props.desc) 
                : 'Pent brukt Iphone X. Kjopt in 2019. Ny pris: 2345 kr. Ma hentes.'}
              </p>               
            </div>
          </div>   
      );
    })
    }
}

export default Product;
