import React, { useEffect, useState } from 'react';
import './FilterBadge.css'
import Badge from 'react-bootstrap/Badge';

const FilterBadge = ({searchParams, removeSelectedFilter}) => {

  const [params, setParams] = useState([]);

    const handleOnClick = (e,key) => {
        e.preventDefault()
        console.log(key)
        setParams([])
        removeSelectedFilter(key)
    } 

    const getParams = () => {    
      for(const [key, value] of searchParams.entries()){
        if(key !== 'q') {
          let param = {key: key, value: value};
            setParams([...params, param])    
        }
      }
    }

    useEffect(() => {
      setParams()
      getParams()
    }, [searchParams]);


  return (
    <div className='badge-container'>
        {params.length > 0 && params.map((item,index) => {
                let msg = item.key + ": " + item.value;
            return(
            <h5 key={index} style={{margin: 5}} onClick={(e) => handleOnClick(e,item.key)}>
              <Badge variant='secondary' >{msg}</Badge>
            </h5>
            )

        })

        }

    </div>
  )
}

export default FilterBadge;
