import React from "react";
import { Link } from "react-router-dom";
import './Login.css';
import { instanceAxs } from "../../api/Api";
import { login, logout } from '../../actions/LoginActions.js';
import { connect } from 'react-redux';

class Login extends React.Component{ 
    constructor(props){
        super(props);
        this.state = {
            user: {
                email: '',
                password: ''
            },
        }
        this.onSubmit = this.onSubmit.bind(this);
    } 

    onSubmit = async (event) => {
        event.preventDefault();
        const email = event.target[0].value;
        const password = event.target[1].value;

        instanceAxs.post('/login', {email, password}).then(response => {
            const responseMessage = response.data.message;
            if (responseMessage !== 'user logged in')   return console.log(responseMessage);

            this.props.history.push('/')
        })


        // this.props.login(this.state.user);   
    }

    logout = () => {
        this.props.logout();
    }
    
    render(){
        return(
            <div className="loginDiv">
                <form id="login-form" onSubmit={this.onSubmit}>
                    <input type="email" placeholder="Email" name='email' required  ></input>
                    <input type="text" placeholder='Password' name='password' required ></input>
                    <button type="submit" >Login</button>
                    <Link to="/register">Register</Link>
                </form>
            </div>
        )
    }
}

const mapDispatchToProps = {
    login, logout
}

export default connect(null, mapDispatchToProps)(Login)
