import './Profile.css';
import React from 'react';
import { instanceAxs } from '../../../config/api.js';
import { getCroppedImage } from '../../../utils/cropImage';
import { dataURLtoFile } from '../../../utils/dataURltoFile';

class Profile extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            profileImgLink: '',
            profileImgBinary: 'null',
            isLoading: false
        }
    }

    uploadImage = async (event) => {
        event.preventDefault()
        const formData = await this.convertImagesToFormData();
        this.uploadImagesToServer(formData);
    }

    onImageChange = async (event) => {
        if(!event.target.files) return;   //target.files seçilen dosyaları döner
      
        const imageArray = [];
        Array.from(event.target.files).forEach(file => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.addEventListener('load', () => imageArray.push({ name: file.name, data: reader.result }))
        })
        const file = event.target.files[0];
        console.log(file)
        
        const convertedFile = await this.convertToBase64(file);
      };

      convertToBase64 = (file) => {
        return new Promise(resolse => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                resolse(reader.result)
            }
        })
      }



    getProfileImage = () => {
        instanceAxs.get('/profile/getprofileimg?image_name=profilePicture.jpeg').then(response => {
            console.log(response)
            this.setState({ isLoading: false, profileImgLink: response.data})
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        // this.getProfileImage();
    }

    render() {
        return(
            <div>
                {!this.state.isLoading ? 
                <div>
                    <input type="file" name="image" accept="image/*"  onChange={this.onImageChange} required></input>
                    <button onClick={this.uploadImage}>Upload</button>  
                    <button onClick={this.convertImagesToFormData}>Show</button>  
                    <img src='http://localhost:3080/profile/getprofileimg?image_name=profilePicture.jpeg' alt="pp" width="500" height="600"/>
                </div>
                :
                <p>Loading</p>}
            </div>         
        )
    }
}

export default Profile;
