import React from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import './Login.css';
import { instanceAxs } from "../../api/Api";
import { userApi } from "../../config";
import { login, logout } from '../../actions/LoginActions.js';
import { connect } from 'react-redux';


axios.defaults.withCredentials = true;

class Login extends React.Component{ 
    constructor(props){
        super(props);
        this.state = {
            user: {
                email: '',
                password: ''
            },
            message: 'Login message here',
            checklog: 'Check login message here',
            logout: 'Logout message here'
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    } 

    handleChange = (event) => {
        let user = this.state.user
        if (event.target.name === 'email') {
            user.email = event.target.value;
        } else if (event.target.name === 'pass') {
            user.password = event.target.value;
        } 
        this.setState({ user });
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        this.props.login(this.state.user);
    }

    checklogin = async () => {
        const response = await instanceAxs.get(`${userApi}/checklogin`);
        console.log(response)
        if(response.data.message === "user logged in"){
            this.setState({ checklog: 'user logged in'});
            console.log(response.data.message)
        } else {
            this.setState({ checklog: 'user not logged in'});
        }
    }

    logout = () => {
        this.props.logout();
    }
    
    render(){
        return(
            <form id="login-form" onSubmit={this.handleSubmit}>
                    <input type="email" placeholder="Email" required onChange={this.handleChange}></input>
                    <input type="text" placeholder='Password' required onChange={this.handleChange}></input>
                <button type="submit" >Login</button>
                
                <Link to="/register">Register</Link>
            </form>
        )
    }
}

const mapDispatchToProps = {
    login, logout
}

export default connect(null, mapDispatchToProps)(Login)
