import React, { useEffect, useState } from 'react'
import { InputGroup } from 'react-bootstrap/'
import { Button, Form } from 'react-bootstrap'
import Autocomplete from '@mui/material/Autocomplete'
import { Input, TextField } from '@mui/material'
import './Searchbar.css';

export default function Searchbar() {
    const [filteredData, setFilteredData] = useState([]);
    const [searchInput, setSearchInput] = useState()
    const data = ['asim', 'ahmet', 'mustafa', 'zeynep'];

    const handleInput = (e) => {
        let input = e.target.value;
        setSearchInput(input)
    }

    useEffect(() => {
        if(searchInput === '') return setFilteredData([]);

        const newData = data.filter(item => {
            return item.includes(searchInput)
         })
         setFilteredData(newData)
    }, [searchInput])

  return (
    <div>
        <div className='search-inputs'>
            <InputGroup>
                    <Form.Control className='search-inputs__input' type='text' placeholder='Søk...' onChange={handleInput} style={{width: 250}}></Form.Control>
            <Button clas style={{width: 80}}>Search</Button>
            </InputGroup>
        </div>
        {filteredData.length > 0 &&
        <div className='search-suggestions'>
            {filteredData.map(item => {
                return(
                    <div className='data-item'>{item}</div>
                )
            })}

        </div>
        }
    </div>
  )
}
